--

CREATE TABLE `tin` (
  `idTin` int(11) NOT NULL DEFAULT current_timestamp(),
  `TieuDe` varchar(255) NOT NULL DEFAULT '',
  `TieuDe_KhongDau` varchar(255) NOT NULL,
  `TomTat` varchar(1000) DEFAULT NULL,
  `urlHinh` varchar(255) DEFAULT NULL,
  `Ngay` date DEFAULT NULL,
  `idUser` int(11) NOT NULL DEFAULT 0,
  `Content` text DEFAULT NULL,
  `idLT` int(11) NOT NULL DEFAULT 0,
  `idTL` int(11) DEFAULT 1,
  `SoLanXem` int(11) DEFAULT 0,
  `TinNoiBat` tinyint(1) DEFAULT 0,
  `AnHien` tinyint(1) DEFAULT 1
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `tin`
--

INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(1, 'Lần đầu ĐH FPT cấp học bổng tiến sĩ ', 'Lan-Dau-Dh-Fpt-Cap-Hoc-Bong-Tien-Si', 'Bên cạnh 400 suất học bổng Nguyễn Văn Đạo, ĐH FPT lần đầu tiên chọn ra 30 học sinh xuất sắc nhất để cấp học bổng toàn phần đào tạo từ cử nhân lên thẳng tiến sĩ, với tổng giá trị quỹ lên tới 5 triệu USD.', 'FPT-2.jpg', NULL, 22, '<p class=\"Normal\">Ng&agrave;y 18/1, ĐH FPT c&ocirc;ng bố chương tr&igrave;nh học bổng mang t&ecirc;n cố gi&aacute;o  sư Nguyễn Văn Đạo v&agrave; học bổng tiến sỹ cho m&ugrave;a tuyển sinh 2011.</p>\r\n<p class=\"Normal\">Trong số 400 học sinh xuất sắc nhất ở 100 trường THPT h&agrave;ng đầu  cả nước được cấp học bổng Nguyễn Văn Đạo, ĐH FPT sẽ chọn ra 30 em giỏi nhất để  cấp học bổng đ&agrave;o tạo bậc tiến sĩ chuy&ecirc;n ng&agrave;nh Khoa học m&aacute;y t&iacute;nh, với thời gian  đ&agrave;o tạo ở cả trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class=\"Normal\">Để được cấp học bổng n&agrave;y, th&iacute; sinh phải c&oacute; th&agrave;nh t&iacute;ch học tập  xuất sắc, đam m&ecirc; ng&agrave;nh CNTT, v&agrave; đặc biệt đam m&ecirc; nghi&ecirc;n cứu khoa học. Ngo&agrave;i ra,  ĐH FPT tiếp tục d&agrave;nh nhiều suất học bổng to&agrave;n phần v&agrave; b&aacute;n phần cho c&aacute;c th&iacute; sinh  đạt kết quả cao trong kỳ thi tuyển sinh của trường, cũng như th&iacute; sinh đạt th&agrave;nh  t&iacute;ch trong c&aacute;c kỳ thi Olympic quốc gia v&agrave; quốc tế.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/58/D9/FPT.jpg\" alt=\"Ảnh: Ho&agrave;ng H&agrave;.\" width=\"480\" height=\"355\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">H&agrave;ng năm, sinh vi&ecirc;n của ĐH FPT đều c&oacute; nhiều đợt sang nước ngo&agrave;i học tập v&agrave; thực tập. Ảnh: <em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo Hiệu trưởng ĐH FPT L&ecirc; Trường T&ugrave;ng, \"đầu ra\" cho những tiến  sĩ CNTT tương lai n&agrave;y sẽ l&agrave; nguồn nh&acirc;n lực cho Viện nghi&ecirc;n cứu C&ocirc;ng nghệ FPT -  nơi tập trung nghi&ecirc;n cứu v&agrave; ứng dụng những dự &aacute;n, sản phẩm c&ocirc;ng nghệ mới c&oacute;  nhiều khả năng thương mại h&oacute;a v&agrave; &aacute;p dụng v&agrave;o thực tiễn. Đồng thời đ&acirc;y sẽ l&agrave;  nguồn cung cho đội ngũ giảng vi&ecirc;n của ĐH FPT, g&oacute;p phần v&agrave;o mục ti&ecirc;u trở th&agrave;nh  trường đại học đ&agrave;o tạo CNTT v&agrave; truyền th&ocirc;ng lớn nhất v&agrave; tốt nhất Việt Nam.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n một trường đại học c&ocirc;ng bố cấp cả g&oacute;i học  bổng đ&agrave;o tạo từ bậc cử nh&acirc;n l&ecirc;n tiến sĩ cho sinh vi&ecirc;n đầu v&agrave;o.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, năm 2010, ĐH FPT cấp gần 200 suất học bổng Nguyễn Văn  Đạo trị gi&aacute; 9.900 USD một suất cho c&aacute;c học sinh xuất sắc tr&ecirc;n cả nước.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn L&ecirc;</strong></p>', 1, 1, 252, 0, 1),
(2, '300 tỷ đồng phát triển giáo dục mầm non ', '300-Ty-Dong-Phat-Trien-Giao-Duc-Mam-Non', 'Bộ Giáo dục và Đào tạo đang xây dựng chương trình, mục tiêu quốc gia về giáo dục giai đoạn 2011-2015, trong đó dự kiến chi 300 tỷ đồng để phát triển giáo dục mầm non năm 2011.\r\n', 'tre_em_set_sub.jpg', NULL, 22, '<div style=\"overflow: hidden;\">\r\n<h1 class=\"Title\">300 tỷ đồng ph&aacute;t triển gi&aacute;o dục mầm non</h1>\r\n<h2 class=\"Lead\">Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đang x&acirc;y dựng chương tr&igrave;nh, mục ti&ecirc;u quốc  gia về gi&aacute;o dục giai đoạn 2011-2015, trong đ&oacute; dự kiến chi 300 tỷ đồng để ph&aacute;t  triển gi&aacute;o dục mầm non năm 2011.</h2>\r\n<p class=\"Normal\">Với số tiền n&agrave;y, Bộ Gi&aacute;o dục sẽ x&acirc;y dựng th&ecirc;m khoảng 16.400  ph&ograve;ng học phục vụ mục ti&ecirc;u phổ cập gi&aacute;o dục mầm non cho trẻ 5 tuổi, sau đ&oacute; từng  bước x&acirc;y dựng đủ số ph&ograve;ng học c&ograve;n thiếu cho gi&aacute;o dục mầm non.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/58/52/tre_em.jpg\" alt=\"\" width=\"495\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Năm 2011 dự kiến gi&aacute;o dục mầm non sẽ được chi 300 tỷ đồng. <em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c thiết bị tối thiểu phục vụ c&ocirc;ng t&aacute;c giảng dạy cho c&aacute;c lớp  học mầm non sẽ được đầu tư; c&aacute;c lớp đ&agrave;o tạo, bồi dưỡng n&acirc;ng cao tr&igrave;nh độ cho  gi&aacute;o vi&ecirc;n mầm non sẽ được ch&uacute; trọng nhằm n&acirc;ng chuẩn đ&agrave;o tạo v&agrave; chuẩn nghề  nghiệp, đ&aacute;p ứng được y&ecirc;u cầu chương tr&igrave;nh gi&aacute;o dục mầm non mới.</p>\r\n<p class=\"Normal\">Hiện Bộ Gi&aacute;o dục cũng đ&atilde; phối hợp với Bộ Kế hoạch v&agrave; đầu tư, Bộ  T&agrave;i ch&iacute;nh x&acirc;y dựng dự &aacute;n gi&aacute;o dục mầm non cho trẻ em c&oacute; ho&agrave;n cảnh kh&oacute; khăn. Theo  đ&oacute;, trong giai đoạn 2011- 2015, khoảng 50 triệu USD sẽ được d&ugrave;ng để đầu tư cho  trẻ thiệt th&ograve;i. Đ&acirc;y l&agrave; kinh ph&iacute; được sử dụng từ nguồn vốn vay ưu đ&atilde;i của Ng&acirc;n  h&agrave;ng thế giới.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n</div>', 1, 1, 2292, 0, 1),
(4, 'Nợ giáo viên tiền tỷ chi phí phổ cập giáo dục ', 'No-Giao-Vien-Tien-Ty-Chi-Phi-Pho-Cap-Giao-Duc', 'Ba năm qua, nhiều giáo viên ở Khánh Hòa bỏ công sức, kể cả tiền bạc để thực hiện phổ cập giáo dục cho học sinh trên địa bàn tỉnh, song đến nay vẫn chưa nhận được tiền chính quyền chi trả.\r\n', 'pho-cap-giao-duc-nho-2.jpg', NULL, 22, '<h1 class=\"Title\">Nợ gi&aacute;o vi&ecirc;n tiền tỷ chi ph&iacute; phổ cập gi&aacute;o dục</h1>\r\n<h2 class=\"Lead\">Ba năm qua, nhiều gi&aacute;o vi&ecirc;n ở Kh&aacute;nh H&ograve;a bỏ c&ocirc;ng sức, kể cả tiền  bạc để thực hiện phổ cập gi&aacute;o dục cho học sinh tr&ecirc;n địa b&agrave;n tỉnh, song đến nay  vẫn chưa nhận được tiền ch&iacute;nh quyền chi trả.</h2>\r\n<p class=\"Normal\">Thầy Nguyễn Ngọc Thạch, Ph&oacute; hiệu trưởng trường tiểu học Trịnh  Phong, thị trấn Di&ecirc;n Kh&aacute;nh (huyện Di&ecirc;n Kh&aacute;nh, Kh&aacute;nh H&ograve;a), cho biết: Nh&agrave; trường  vẫn c&ograve;n thiếu hơn 260 triệu đồng chi trả cho 66 lượt gi&aacute;o vi&ecirc;n trong 3 năm qua,  nhiều văn ph&ograve;ng phẩm d&ugrave;ng để giảng dạy cũng do gi&aacute;o vi&ecirc;n tự bỏ tiền t&uacute;i ra. Hiện  tại, nh&agrave; trường c&oacute; nhiều thầy c&ocirc; chưa được chi trả tiền đứng lớp phổ cập từ 10  đến 50 triệu đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/pho-cap-giao-duc-to.jpg\" alt=\"\" width=\"490\" height=\"350\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều gi&aacute;o vi&ecirc;n Kh&aacute;nh H&ograve;a đang bị nợ tiền phổ cập gi&aacute;o dục. Ảnh  minh họa: <em>Nam Điền</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chia sẻ kh&oacute; khăn tr&ecirc;n, c&ocirc; gi&aacute;o B&ugrave;i Thị Luyến, gi&aacute;o vi&ecirc;n dạy To&aacute;n  của trường tiểu học Trịnh Phong cho biết: Từ khi c&oacute; phong tr&agrave;o dạy phổ cập gi&aacute;o  dục c&ocirc; đ&atilde; tham gia giảng dạy c&aacute;c lớp phổ cập gi&aacute;o dục v&agrave;o ban đ&ecirc;m, mỗi tuần tới  5 tiết. Thời gian trước chế độ đứng lớp đều được thanh to&aacute;n r&otilde; r&agrave;ng. Từ năm 2007  đến nay chưa được thanh to&aacute;n tiền đứng lớp, c&ocirc; bị nợ đến hơn 20 triệu đồng.</p>\r\n<p class=\"Normal\">C&ocirc; Luyến ngậm ng&ugrave;i: &ldquo;Việc tham gia c&ocirc;ng t&aacute;c phổ cập gi&aacute;o dục l&agrave;  một ti&ecirc;u ch&iacute; b&igrave;nh x&eacute;t thi đua h&agrave;ng năm n&ecirc;n phải cố gắng. Bằng l&ograve;ng nhiệt t&igrave;nh,  t&ocirc;i cũng cố gắng bỏ c&ocirc;ng sức, xăng xe mấy năm liền đi dạy. Tuy nhi&ecirc;n, việc chậm  thanh to&aacute;n l&agrave; rất thiệt th&ograve;i, hiện gia đ&igrave;nh t&ocirc;i c&ograve;n một người mẹ v&agrave; nu&ocirc;i hai con  ăn học n&ecirc;n kinh tế kh&aacute; eo hẹp&rdquo;.</p>\r\n<p class=\"Normal\">&Ocirc;ng B&ugrave;i Hữu H&oacute;a, Trưởng ph&ograve;ng gi&aacute;o dục huyện miền n&uacute;i Kh&aacute;nh  Vĩnh, giải th&iacute;ch: \"Kh&aacute;nh Vĩnh l&agrave; địa b&agrave;n miền n&uacute;i, do đ&oacute; việc vận động con em  đồng b&agrave;o d&acirc;n tộc &iacute;t người đi học gặp nhiều kh&oacute; khăn, lại thiếu kinh ph&iacute; hỗ trợ  n&ecirc;n c&aacute;c hoạt động n&agrave;y li&ecirc;n tục nhiều năm đ&atilde; &iacute;t nhiều g&acirc;y th&ecirc;m kh&oacute; khăn, ức chế  cho c&ocirc;ng t&aacute;c giảng dạy của gi&aacute;o vi&ecirc;n\". Hiện tại, huyện Kh&aacute;nh Vĩnh đang thiếu của  gi&aacute;o vi&ecirc;n gần 400 triệu đồng phổ cập gi&aacute;o dục.</p>\r\n<p class=\"Normal\">Trao đổi về vấn đề tr&ecirc;n, &ocirc;ng Lưu Quốc Thanh, Gi&aacute;m đốc Sở Gi&aacute;o  dục v&agrave; Đ&agrave;o tạo tỉnh Kh&aacute;nh H&ograve;a x&aacute;c nhận, ng&agrave;nh gi&aacute;o dục Kh&aacute;nh H&ograve;a đang cần bổ  sung gần 7,5 tỷ đồng tiền phục vụ c&ocirc;ng t&aacute;c phổ cập gi&aacute;o dục. Đ&acirc;y l&agrave; số tiền c&ograve;n  thiếu của gi&aacute;o vi&ecirc;n trong 3 năm từ 2008 đến 2010 tr&ecirc;n địa b&agrave;n to&agrave;n tỉnh. Sở đ&atilde;  c&oacute; c&ocirc;ng văn gửi Sở T&agrave;i ch&iacute;nh đề nghị bổ sung ng&acirc;n s&aacute;ch để hỗ trợ cho địa phương  chi trả khoản tiền tr&ecirc;n cho c&aacute;c gi&aacute;o vi&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nam Điền</strong></p>', 1, 1, 34, 0, 1),
(5, 'Đón và chăm sóc trẻ sau giờ tan trường qua dịch vụ ', 'Don-Va-Cham-Soc-Tre-Sau-Gio-Tan-Truong-Qua-Dich-Vu', 'Các bé sẽ được chăm sóc bữa ăn, tắm rửa sạch sẽ, vui chơi và học tập cùng cô giáo theo các nội dung trong sổ báo bài, mở rộng hoặc đào sâu kiến thức theo yêu cầu của phụ huynh.\r\n \r\n', 'be-2.jpg', NULL, 22, '<h1 class=\"Title\">Đ&oacute;n v&agrave; chăm s&oacute;c trẻ sau giờ tan trường qua dịch  vụ</h1>\r\n<h2 class=\"Lead\">C&aacute;c b&eacute; sẽ được chăm s&oacute;c bữa ăn, tắm rửa sạch sẽ, vui  chơi v&agrave; học tập c&ugrave;ng c&ocirc; gi&aacute;o theo c&aacute;c nội dung trong sổ b&aacute;o b&agrave;i, mở rộng hoặc  đ&agrave;o s&acirc;u kiến thức theo y&ecirc;u cầu của phụ huynh.</h2>\r\n<p class=\"Normal\">Với dịch vụ &ldquo;Đưa - đ&oacute;n v&agrave; chăm s&oacute;c b&eacute; sau giờ tan  trường&rdquo;, Happy Back Home thay cha mẹ đ&oacute;n trẻ tại trường bằng &ocirc;t&ocirc; của trung t&acirc;m,  nắm bắt th&ocirc;ng tin học tập, th&ocirc;ng b&aacute;o mới của trường để cung cấp lại đầy đủ cho  phụ huynh.</p>\r\n<p class=\"Normal\">Lịch sinh hoạt của b&eacute; tại trung t&acirc;m được x&acirc;y dựng hợp  l&yacute; v&agrave; phong ph&uacute; dựa theo t&acirc;m sinh l&yacute; mỗi lứa tuổi. Mọi chương tr&igrave;nh đều được  thống nhất giữa gia đ&igrave;nh v&agrave; trung t&acirc;m, kh&ocirc;ng l&agrave;m x&aacute;o trộn cuộc sống thường nhật  ở nh&agrave; của trẻ. C&aacute;c b&eacute; được vui chơi sau một ng&agrave;y học, lấy lại năng lượng từ  những bữa ăn chất lượng, ph&ugrave; hợp sở th&iacute;ch. Ngo&agrave;i ra, trẻ được gi&aacute;o vi&ecirc;n k&egrave;m học  với phương ph&aacute;p sư phạm tiến bộ gi&uacute;p b&eacute; dễ hiểu v&agrave; nhớ b&agrave;i l&acirc;u.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/_DSC3170.jpg\" alt=\"\" width=\"448\" height=\"298\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, dịch vụ &ldquo;Du lịch sinh th&aacute;i - Từ trường  học đến cuộc sống&rdquo; sẽ gi&uacute;p trẻ củng cố kiến thức l&yacute; thuyết bằng những chuyến du  lịch sinh th&aacute;i được đ&uacute;c kết từ ch&iacute;nh c&aacute;c b&agrave;i học tại trường. Th&ocirc;ng qua buổi d&atilde;  ngoại, tham quan những địa điểm du lịch, mỗi chương tr&igrave;nh sẽ gi&uacute;p b&eacute; được tận  mắt v&agrave; chạm v&agrave;o điều đ&atilde; học. Đồng thời, gi&aacute;o vi&ecirc;n chuy&ecirc;n m&ocirc;n sẽ gi&uacute;p c&aacute;c con  hiểu r&otilde; v&agrave; s&acirc;u sắc hơn c&aacute;c điều đ&atilde; thấy.</p>\r\n<p class=\"Normal\">Với &ldquo;R&egrave;n luyện kỹ năng sống - B&eacute; với Cộng đồng&rdquo;,  Happy Back Home chủ trương kh&ocirc;ng d&ugrave;ng gi&aacute;o tr&igrave;nh phức tạp m&agrave; chỉ tổng hợp c&aacute;c  b&agrave;i học đạo đức trong chương tr&igrave;nh học của trẻ. Bằng những buổi hoạt động ngoại  kh&oacute;a c&ugrave;ng bạn b&egrave;, trẻ dễ d&agrave;ng tiếp nhận v&agrave; hiểu những gi&aacute; trị rất đời thường. Từ  đ&oacute;, b&eacute; tự x&acirc;y dựng v&agrave; n&acirc;ng dần c&aacute;c kỹ năng sống cơ bản nhưng quan trọng. Điều  n&agrave;y g&oacute;p phần h&igrave;nh th&agrave;nh nh&acirc;n c&aacute;ch v&agrave; tạo tiền đề ph&aacute;t triển tương lai cho b&eacute;.  Ngo&agrave;i ra, trung t&acirc;m c&ograve;n cung cấp dịch vụ chăm s&oacute;c b&eacute; ng&agrave;y lễ, ng&agrave;y nghỉ đột xuất  v&agrave; c&aacute;c chương tr&igrave;nh &ldquo;Ng&agrave;y thứ bảy của b&eacute;\" phong ph&uacute;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"10\" height=\"22\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/P1000817.jpg\" alt=\"\" width=\"448\" height=\"336\" /><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nh&acirc;n Tết Nguy&ecirc;n đ&aacute;n 2011, từ 24 đến 31/1, Happy Back  Home cung cấp dịch vụ chăm s&oacute;c b&eacute; ng&agrave;y cận Tết với mức gi&aacute; ưu đ&atilde;i 15%. C&aacute;c b&eacute; sẽ  được tham gia v&agrave;o c&aacute;c chương tr&igrave;nh sinh hoạt với chủ đề về Tết cổ truyền bằng  một chuỗi c&aacute;c c&acirc;u chuyện, tr&ograve; chơi li&ecirc;n quan đến Tết. Ngo&agrave;i ra, chương tr&igrave;nh &ldquo;Du  lịch sinh th&aacute;i - Từ trường học đến cuộc sống&rdquo; số đầu ti&ecirc;n nh&acirc;n dịp Tết Nguy&ecirc;n  Đ&aacute;n 2011 với chủ đề &ldquo;Về nguồn&rdquo; sẽ đưa b&eacute; đến thăm l&agrave;ng nghề truyền thống Việt  Nam.</p>\r\n<p class=\"Normal\">Dịch vụ dựa tr&ecirc;n m&ocirc; h&igrave;nh &ldquo;After school care&rdquo; đang thịnh h&agrave;nh tại  c&aacute;c quốc gia ph&aacute;t triển. Truy cập <a class=\"Normal\" href=\"http://www.happybackhome.com/\">http://www.happybackhome.com</a> hoặc li&ecirc;n  hệ 08-6296 6792 để c&oacute; th&ecirc;m th&ocirc;ng tin.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn: <em>Happy Back Home</em>)</p>', 1, 1, 2279, 0, 1),
(6, '7 học sinh rơi từ tầng hai xuống đất vì gãy lan can ', '7-Hoc-Sinh-Roi-Tu-Tang-Hai-Xuong-Dat-Vi-Gay-Lan-Can', 'Đang giờ ra chơi, bất ngờ toàn bộ lan can tầng hai của Trường THCS thị trấn Chợ Rã (Bắc Kạn) gãy đổ ra ngoài, kéo theo 7 học sinh lớp 6A rơi xuống đất. \r\n', 'tai_nan_set_top.gif', NULL, 22, '<p>Đang giờ ra chơi, bất ngờ to&agrave;n bộ lan can tầng hai của Trường THCS thị trấn Chợ  R&atilde; (Bắc Kạn) g&atilde;y đổ ra ngo&agrave;i, k&eacute;o theo 7 học sinh lớp 6A rơi xuống đất.</p>\r\n<p class=\"Normal\">Theo l&atilde;nh đạo trường, l&uacute;c đ&oacute; l&agrave; giờ ra chơi chiều 10/1, 7 học  sinh đang đứng dựa v&agrave;o lan can. Sau tai nạn c&aacute;c em đ&atilde; được đưa v&agrave;o Bệnh viện  huyện Ba Bể cấp cứu. Tuy nhi&ecirc;n c&oacute; 4 em bị thương nặng gồm Nguyễn T&ugrave;ng L&acirc;m, N&ocirc;ng  Thị Ngọc, N&ocirc;ng Thị Thảo Trang v&agrave; Dương Thị H&agrave; Vy phải chuyển l&ecirc;n Bệnh vi&ecirc;n Đa  khoa tỉnh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/tai_nan.gif\" alt=\"\" width=\"400\" height=\"312\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&aacute;u Nguyễn T&ugrave;ng L&acirc;m, 11 tuổi bị gẫy 02 xương cẳng tay phải, đa  vết thương răng h&agrave;m mặt đ&atilde; được b&oacute; bột v&agrave; chuyển chắm s&oacute;c  ri&ecirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&aacute;c sĩ L&ocirc; Văn Tường, Trưởng khoa Ngoại chấn thương của bệnh viện  cho biết, em Nguyễn T&ugrave;ng L&acirc;m bị g&atilde;y hai xương cẳng tay phải, đa vết thương răng  h&agrave;m mặt đ&atilde; được b&oacute; bột v&agrave; chăm s&oacute;c ri&ecirc;ng.</p>\r\n<p class=\"Normal\">Theo chuẩn đo&aacute;n ban đầu, em N&ocirc;ng Thị Ngọc bị gẫy k&iacute;n 2 xương  c&aacute;nh tay phải; N&ocirc;ng Thị Thảo Trang bị chấn thương cột sống, l&uacute;n L1, kh&ocirc;ng liệt;  Dương Thị H&agrave; Vy bị đa vết thương răng h&agrave;m mặt v&agrave; ch&acirc;n phải.</p>\r\n<p class=\"Normal\">Sau vụ tai nạn, l&atilde;nh đạo tỉnh Bắc Kạn đ&atilde; trực tiếp đến thăm c&aacute;c  học sinh gặp nạn. Chủ tịch UBND tỉnh đ&atilde; chỉ đạo c&aacute;c ng&agrave;nh chức năng l&agrave;m r&otilde;  nguy&ecirc;n nh&acirc;n vụ việc, nhất l&agrave; chất lượng c&ocirc;ng tr&igrave;nh trường THCS thị trấn Chợ R&atilde;  để xử l&yacute;.<br /><br />Trường THCS thị trấn Chợ R&atilde; do C&ocirc;ng ty Ph&uacute; Đạt (Th&aacute;i Nguy&ecirc;n)  thi c&ocirc;ng từ năm 2006 đến nay vẫn chưa ho&agrave;n th&agrave;nh. C&aacute;c lớp học đ&atilde; đưa v&agrave;o sử dụng  trước đ&oacute; cũng chưa c&oacute; lan can cầu thang.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Theo <em>B&aacute;o Bắc Kạn</em>)</p>', 1, 1, 1081, 0, 1),
(7, 'Giáo viên TP HCM được thưởng tết tối thiểu 700.000 đồng ', 'Giao-Vien-Tp-Hcm-Duoc-Thuong-Tet-Toi-Thieu-700.000-Dong', 'Sở GD&ĐT TP HCM vừa có thông báo về việc UBND thành phố chấp thuận đề nghị hỗ trợ mức quà tết cho cán bộ công chức trong ngành tối thiểu là 700.000 đồng. Mức thưởng này cao hơn năm ngoái 100.000 đồng.\r\n', 'thuong-tet-3.jpg', NULL, 22, '<h1 class=\"Title\">Gi&aacute;o vi&ecirc;n TP HCM được thưởng tết tối thiểu 700.000 đồng</h1>\r\n<h2 class=\"Lead\">Sở GD&amp;ĐT TP HCM vừa c&oacute; th&ocirc;ng b&aacute;o về việc UBND th&agrave;nh phố chấp  thuận đề nghị hỗ trợ mức qu&agrave; tết cho c&aacute;n bộ c&ocirc;ng chức trong ng&agrave;nh tối thiểu l&agrave;  700.000 đồng. Mức thưởng n&agrave;y cao hơn năm ngo&aacute;i 100.000 đồng.</h2>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, đối với những đơn vị trường học tự chủ về t&agrave;i ch&iacute;nh  tự c&acirc;n đối thu chi th&igrave; mức thưởng d&agrave;nh cho gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n c&oacute; thể  cao hơn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/thuong-tet-1.jpg\" alt=\"\" width=\"480\" height=\"361\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gi&aacute;o vi&ecirc;n hướng dẫn học sinh trong kỳ thi tốt nghiệp 2010. Ảnh:  <em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Thực tế ở một số trường học v&agrave; cấp học mức thưởng kh&ocirc;ng giống  nhau t&ugrave;y v&agrave;o nguồn thu của nh&agrave; trường. Ở một số trường PTTH, THCS, tiểu học gi&aacute;o  vi&ecirc;n cho biết, mức thưởng năm nay cũng tương tự năm ngo&aacute;i c&oacute; thể từ 500 đến v&agrave;i  triệu đồng t&ugrave;y v&agrave;o th&acirc;m ni&ecirc;n hoặc sự đ&aacute;nh gi&aacute; của nh&agrave; trường.</p>\r\n<p class=\"Normal\">C&ograve;n đối với những gi&aacute;o vi&ecirc;n mầm non ở c&aacute;c nh&oacute;m trường tư th&igrave; vẫn  chưa c&oacute; th&ocirc;ng tin g&igrave; về việc thưởng Tết. \"Năm n&agrave;o cũng vậy cả th&ocirc;i, gọi l&agrave;  thưởng cho đỡ tủi th&acirc;n chứ 2-3 trăm ng&agrave;n th&igrave; kh&ocirc;ng đủ đi chợ một bữa\", một nữ  gi&aacute;o vi&ecirc;n gi&atilde;i b&agrave;y.</p>\r\n<p class=\"Normal\">\"Những trường c&oacute; nguồn thu ri&ecirc;ng th&igrave; c&oacute; thể thưởng cho c&aacute;n bộ  gi&aacute;o vi&ecirc;n v&agrave;i triệu hoặc nhiều hơn, c&ograve;n những trường kh&ocirc;ng c&oacute; nguồn thu, học  sinh &iacute;t th&igrave; cũng đ&agrave;nh phải chịu chứ kh&ocirc;ng c&oacute; th&igrave; lấy đ&acirc;u ra m&agrave; thưởng. Việc  lương thưởng c&aacute;c c&aacute;n bộ gi&aacute;o vi&ecirc;n c&oacute; thể thỏa thuận với nh&agrave; trường theo thỏa ước  hay hợp đồng lao động\", &ocirc;ng Nguyễn Hữu H&ugrave;ng Chủ tịch c&ocirc;ng đo&agrave;n Sở GD&amp;ĐT TP  HCM n&oacute;i.</p>\r\n<p class=\"Normal\">&Ocirc;ng H&ugrave;ng cho biết th&ecirc;m, để hỗ trợ những c&aacute;n bộ gi&aacute;o vi&ecirc;n c&oacute; ho&agrave;n  cảnh kh&oacute; khăn ở c&aacute;c quận huyện ngoại th&agrave;nh ăn Tết, Sở đ&atilde; ph&aacute;t động phong tr&agrave;o  \"nội th&agrave;nh hỗ trợ ngoại th&agrave;nh\" v&agrave; sẽ đi thăm tặng qu&agrave; cho những gi&aacute;o vi&ecirc;n ở v&ugrave;ng  ven th&agrave;nh phố.</p>\r\n<p class=\"Normal\">Năm 2010 nhiều trường ở TP HCM gi&aacute;o vi&ecirc;n c&oacute; mức thưởng Tết l&ecirc;n  đến h&agrave;ng chục triệu đồng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 1, 2811, 0, 1),
(8, 'Mức sinh hoạt phí tối đa cho lưu học sinh là 1.200 USD ', 'Muc-Sinh-Hoat-Phi-Toi-Da-Cho-Luu-Hoc-Sinh-La-1.200-Usd', 'Đối với lưu học sinh tại Ba Lan, Bungary, Nga..., mức sinh hoạt phí sẽ tăng từ 400 USD lên 480 USD; tại Australia, New Zealand tăng từ 860 USD lên 1.032 USD và tại Mỹ, Canada, Anh, Nhật Bản tăng từ 1.000 lên 1.200 USD một người một tháng... \r\n', 'du_hoc_sinh_set_sub.jpg', NULL, 22, '<h1 class=\"Title\">Mức sinh hoạt ph&iacute; tối đa cho lưu học sinh l&agrave; 1.200 USD</h1>\r\n<h2 class=\"Lead\">Đối với lưu học sinh tại Ba Lan, Bungary, Nga..., mức sinh hoạt  ph&iacute; sẽ tăng từ 400 USD l&ecirc;n 480 USD; tại Australia, New Zealand tăng từ 860 USD  l&ecirc;n 1.032 USD v&agrave; tại Mỹ, Canada, Anh, Nhật Bản tăng từ 1.000 l&ecirc;n 1.200 USD một  người một th&aacute;ng...</h2>\r\n<p class=\"Normal\">Li&ecirc;n Bộ T&agrave;i ch&iacute;nh, Bộ Ngoại giao, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo vừa ban  h&agrave;nh th&ocirc;ng tư li&ecirc;n tịch hướng dẫn chế độ, cấp ph&aacute;t v&agrave; quản l&yacute; kinh ph&iacute; đ&agrave;o tạo  lưu học sinh Việt Nam ở nước ngo&agrave;i bằng ng&acirc;n s&aacute;ch nh&agrave; nước.</p>\r\n<p class=\"Normal\">Theo đ&oacute;, từ th&aacute;ng 2 mức sinh hoạt ph&iacute; cho học sinh du học bằng  ng&acirc;n s&aacute;ch nh&agrave; nước tại hơn 30 nước v&agrave; v&ugrave;ng l&atilde;nh thổ đều tăng đ&aacute;ng kể. Tăng cao  nhất l&agrave; tại Australia, New Zealand từ 860 USD l&ecirc;n 1.032 USD một người một  th&aacute;ng.</p>\r\n<p class=\"Normal\">Văn bản n&agrave;y cũng bổ sung lưu học sinh ở Đ&agrave;i Loan, Hong Kong,  M&ocirc;ng Cổ v&agrave;o danh mục c&aacute;c nước v&agrave; v&ugrave;ng l&atilde;nh thổ được hỗ trợ bảo hiểm y tế với số  tiền tương ứng l&agrave; 300 USD một người mỗi năm (ri&ecirc;ng ở M&ocirc;ng Cổ l&agrave; 150 USD).</p>\r\n<p class=\"Normal\">\"Ri&ecirc;ng tiền tạm ứng chi ph&iacute; đi đường, ph&iacute; l&agrave;m hộ chiếu, visa v&agrave;  sinh hoạt ph&iacute; (kh&ocirc;ng qu&aacute; 3 th&aacute;ng tiền sinh hoạt ph&iacute;) cho lưu học sinh trước khi  đi học thực hiện theo phương thức cấp bằng tiền mặt\", th&ocirc;ng tư n&ecirc;u r&otilde;.</p>\r\n<p class=\"Normal\">Theo Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, khoảng 60.000 học sinh  Việt Nam đang theo học tại c&aacute;c cơ sở đ&agrave;o tạo nước ngo&agrave;i. Trong đ&oacute; hơn 4.000  người học bằng ng&acirc;n s&aacute;ch nh&agrave; nước theo Đề &aacute;n 322, v&agrave;i ngh&igrave;n người theo học bằng  c&aacute;c học bổng kh&aacute;c, số c&ograve;n lại đi bằng con đường tự t&uacute;c.</p>\r\n<p><strong>\r\n<p class=\"Normal\">Chi tiết sinh hoạt ph&iacute; ở c&aacute;c nước theo th&ocirc;ng tư  mới:</p>\r\n</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\" width=\"486\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\" rowspan=\"2\" width=\"237\" align=\"left\">T&ecirc;n nước v&agrave; v&ugrave;ng l&atilde;nh thổ</td>\r\n<td class=\"BreakLine\" colspan=\"2\" width=\"159\"><br /></td>\r\n<td class=\"Normal\" rowspan=\"2\" width=\"90\" align=\"left\">Mức cấp b&ugrave; SHP diện Hiệp  định (USD/ LHS/ th&aacute;ng)</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"83\" align=\"left\">Bằng đồng đ&ocirc;la Mỹ</td>\r\n<td class=\"Normal\" width=\"76\" align=\"left\">Bằng đồng EURO</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ấn Độ</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">420</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">300-420</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Trung Quốc</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">420</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">293</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Đ&agrave;i Loan (Trung Quốc)</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">420</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Campuchia, L&agrave;o</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">204</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">84</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">M&ocirc;ng Cổ</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">204</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">144</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">H&agrave;n Quốc, Singapore</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">600</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Hong Kong</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">600</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Th&aacute;i Lan, Philippines, Malaysia, Indonesia</td>\r\n<td width=\"83\">\r\n<p class=\"Normal\">360</p>\r\n</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ba Lan</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">480</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Bungary</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">404</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Hungary</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">143-403</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Czech</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">84</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Slovakia</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">112</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Rumany</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">420</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ucraina, Belarutsia</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">456</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Nga</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">480</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">420</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Cuba</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">204</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">198</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">C&aacute;c nước T&acirc;y Bắc &Acirc;u</td>\r\n<td class=\"BreakLine\" width=\"83\"><br /></td>\r\n<td class=\"Normal\" width=\"76\" align=\"middle\">888</td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Mỹ, Canada, Anh, Nhật Bản</td>\r\n<td width=\"83\">\r\n<p class=\"Normal\">1.200</p>\r\n</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Australia, New Zealand</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">1.032</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"BreakLine\" width=\"90\"><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Ai Cập</td>\r\n<td class=\"Normal\" width=\"83\" align=\"middle\">540</td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">480</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"237\">Maroc, Mozambique</td>\r\n<td class=\"BreakLine\" width=\"83\"><br /></td>\r\n<td class=\"BreakLine\" width=\"76\"><br /></td>\r\n<td class=\"Normal\" width=\"90\" align=\"middle\">250</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1, 2115, 0, 1),
(9, 'Học sinh Hà Nội được nghỉ 11 ngày Tết ', 'Hoc-Sinh-Ha-Noi-Duoc-Nghi-11-Ngay-Tet', 'UBND thành phố Hà Nội vừa đồng ý với đề xuất của Sở Giáo dục và Đào tạo về việc cho học sinh nghỉ tết Tết Nguyên đán Tân Mão 11 ngày. \r\n', 't2.jpg', NULL, 22, '<h1 class=\"Title\">Học sinh H&agrave; Nội được nghỉ 11 ng&agrave;y Tết</h1>\r\n<h2 class=\"Lead\">UBND th&agrave;nh phố H&agrave; Nội vừa đồng &yacute; với đề xuất của Sở Gi&aacute;o dục v&agrave;  Đ&agrave;o tạo về việc cho học sinh nghỉ tết Tết Nguy&ecirc;n đ&aacute;n T&acirc;n M&atilde;o 11 ng&agrave;y.</h2>\r\n<p class=\"Normal\">Cụ thể, c&aacute;n bộ, gi&aacute;o vi&ecirc;n, học sinh c&aacute;c trường mầm non, tiểu  học, THCS, THPT, bổ t&uacute;c văn h&oacute;a v&agrave; trung t&acirc;m gi&aacute;o dục kỹ thuật tổng hợp, trung  t&acirc;m gi&aacute;o dục thường xuy&ecirc;n được nghỉ từ ng&agrave;y 28/1 (ng&agrave;y 25 th&aacute;ng chạp) đến hết  ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng gi&ecirc;ng năm T&acirc;n M&atilde;o).<br /><br />C&aacute;n bộ, gi&aacute;o vi&ecirc;n, học sinh  c&aacute;c trường trung cấp chuy&ecirc;n nghiệp được nghỉ 17 ng&agrave;y, từ 22/1 (ng&agrave;y 19 th&aacute;ng  chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng gi&ecirc;ng).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/hs-gialanh10.jpg\" alt=\"\" width=\"495\" height=\"330\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Học sinh H&agrave; Nội đang chịu đợt r&eacute;t đậm. Ảnh: <em>L&ecirc;  Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&ocirc;ng chức, vi&ecirc;n chức trong c&aacute;c cơ quan h&agrave;nh ch&iacute;nh sự nghiệp như  Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo v&agrave; c&aacute;c ph&ograve;ng gi&aacute;o dục sẽ đi l&agrave;m v&agrave;o thứ bảy, chủ nhật  ng&agrave;y 29-30/1, tức ng&agrave;y 26, 27 th&aacute;ng chạp v&agrave; nghỉ Tết bắt đầu từ 31/1 đến 7/2 (từ  28 th&aacute;ng chạp đến hết m&ugrave;ng 5 th&aacute;ng gi&ecirc;ng năm T&acirc;n M&atilde;o).</p>\r\n<p class=\"Normal\">UBND H&agrave; Nội y&ecirc;u cầu Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo th&ocirc;ng b&aacute;o cụ thể về  thời gian nghỉ Tết để nh&agrave; trường, phụ huynh, học sinh thực hiện.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, Thủ tướng cũng đ&atilde; quyết định cho c&ocirc;ng chức, vi&ecirc;n chức  cả nước được nghỉ Tết nguy&ecirc;n đ&aacute;n trong 8 ng&agrave;y, đi l&agrave;m b&ugrave; v&agrave;o thứ bảy, chủ nhật  ng&agrave;y 29-30/1.</p>\r\n<p class=\"Normal\">H&agrave; Nội v&agrave; c&aacute;c tỉnh ph&iacute;a bắc đang g&aacute;nh chịu đợt r&eacute;t đậm, r&eacute;t hại  d&agrave;i, dự kiến đến s&aacute;t tết Nguy&ecirc;n đ&aacute;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 1, 1, 2656, 0, 1),
(10, 'Hàng trăm nghìn học sinh nghỉ học vì giá rét ', 'Hang-Tram-Nghin-Hoc-Sinh-Nghi-Hoc-Vi-Gia-Ret', 'Sớm nay, các trường tiểu học ở Hà Nội đều trưng biển thông báo nghỉ học do nhiệt độ ở mức 8 độ C. Một vài phụ huynh không theo dõi dự báo thời tiết vẫn đưa con đến trường và ngậm ngùi quay xe ra về.\r\n', 'phu_huynh_xem_lich_nghi_hoc_set_sub.jpg', NULL, 22, '<h1 class=\"Title\">H&agrave;ng trăm ngh&igrave;n học sinh nghỉ học v&igrave; gi&aacute; r&eacute;t</h1>\r\n<h2 class=\"Lead\">Sớm nay, c&aacute;c trường tiểu học ở H&agrave; Nội đều trưng biển th&ocirc;ng b&aacute;o  nghỉ học do nhiệt độ ở mức 8 độ C. Một v&agrave;i phụ huynh kh&ocirc;ng theo d&otilde;i dự b&aacute;o thời  tiết vẫn đưa con đến trường v&agrave; ngậm ng&ugrave;i quay xe ra về.</h2>\r\n<p class=\"Normal\">Chị Cao Thị Hồng, Hiệu trưởng Trường tiểu học Mai Dịch (Cầu  Giấy) cho biết, từ s&aacute;ng sớm, chị v&agrave; c&aacute;c c&ocirc; li&ecirc;n tục nhận được điện thoại của phụ  huynh hỏi về việc nghỉ học của con em. Bản th&acirc;n chị đ&atilde; chỉ đạo c&aacute;c c&ocirc; gi&aacute;o chủ  nhiệm chủ động nhắn tin b&aacute;o nghỉ cho phụ huynh.</p>\r\n<p class=\"Normal\">\"Chỉ c&oacute; v&agrave;i ch&aacute;u kh&ocirc;ng theo d&otilde;i được dự b&aacute;o thời tiết v&agrave; chưa  kịp nhận tin nhắn n&ecirc;n vẫn đến lớp. Sau khi nghe th&ocirc;ng b&aacute;o, c&aacute;c ch&aacute;u được phụ  huynh đưa về nh&agrave;. Đến khoảng 8h kh&ocirc;ng c&ograve;n học sinh đến trường nữa\", chị Hồng cho  hay.</p>\r\n<p class=\"Normal\">Hiệu trưởng Hồng cũng chia sẻ, để đối ph&oacute; với thời tiết diễn  biến phức tạp, nh&agrave; trường đ&atilde; l&agrave;m sổ li&ecirc;n lạc điện tử, c&oacute; bất kỳ thay đổi n&agrave;o đều  th&ocirc;ng b&aacute;o nhanh ch&oacute;ng đến từng phụ huynh. Trường cũng qu&aacute;n triệt văn bản chỉ đạo  của Sở đến học sinh v&agrave; phụ huynh, nếu nhiệt độ dưới 10 độ C (theo đ&agrave;i HTV) th&igrave;  phụ huynh chủ động cho con nghỉ học.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/thong_bao_nghi_hoc.jpg\" alt=\"\" width=\"350\" height=\"480\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">S&aacute;ng nay, hầu hết c&aacute;c trường tiểu học đều th&ocirc;ng b&aacute;o cho học sinh  nghỉ học v&igrave; qu&aacute; lạnh. <em>Ảnh: Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&oacute; tr&ecirc;n 3.000 học sinh nhưng s&aacute;ng nay Trường tiểu học Nam Th&agrave;nh  C&ocirc;ng (Đống Đa) chỉ c&oacute; v&agrave;i em đến lớp. Số n&agrave;y được c&aacute;c c&ocirc; giải th&iacute;ch l&agrave; \"trời qu&aacute;  lạnh n&ecirc;n được nghỉ học\".</p>\r\n<p class=\"Normal\">Hiệu trưởng Phan Kim Anh cho biết, từ đầu m&ugrave;a đ&ocirc;ng, trường đ&atilde; c&oacute;  kế hoạch th&ocirc;ng b&aacute;o nghỉ học nếu nhiệt độ xuống dưới mức cho ph&eacute;p. C&aacute;c c&ocirc; chủ  nhiệm đ&atilde; trao đổi với phụ huynh, chủ động theo d&otilde;i dự b&aacute;o thời tiết tr&ecirc;n VTV1 để  l&agrave;m căn cứ.</p>\r\n<p class=\"Normal\">\"Ph&ograve;ng Gi&aacute;o dục quy định theo d&otilde;i chương tr&igrave;nh thời tiết tr&ecirc;n  VTV1 bởi chỉ c&oacute; một mốc nhiệt độ duy nhất để nh&agrave; trường v&agrave; phụ huynh c&ugrave;ng thống  nhất. Ở c&aacute;c đ&agrave;i kh&aacute;c nhiệt độ dự b&aacute;o c&oacute; sự ch&ecirc;nh lệch từ thấp nhất đến cao nhất  n&ecirc;n kh&ocirc;ng biết lựa chọn mốc n&agrave;o l&agrave;m chuẩn\", chị Kim Anh n&oacute;i.</p>\r\n<p class=\"Normal\">Theo chị Kim Anh, khi xem dự b&aacute;o thời tiết tr&ecirc;n VTV1 v&agrave;o l&uacute;c hơn  6h, nh&agrave; trường v&agrave; c&aacute;c c&ocirc; c&ugrave;ng thống nhất để c&oacute; quyết định, sau đ&oacute; th&ocirc;ng b&aacute;o đến  phụ huynh trước 6h30. Như vậy sẽ kh&ocirc;ng s&aacute;t thời gian c&aacute;c ch&aacute;u đi học. \"Từ đầu  m&ugrave;a đ&ocirc;ng đến nay, h&ocirc;m nay l&agrave; ng&agrave;y đầu ti&ecirc;n trường cho học sinh nghỉ. D&ugrave; vậy, c&aacute;c  c&ocirc; vẫn đến trường l&agrave;m điểm, l&agrave;m sổ s&aacute;ch chuẩn bị kết th&uacute;c học kỳ\", chị Kim Anh  cho hay.</p>\r\n<p class=\"Normal\">Chiều h&ocirc;m qua, học sinh Trường tiểu học Phương Mai (Đống Đa) đ&atilde;  nhận được th&ocirc;ng b&aacute;o kết th&uacute;c buổi học chiều sớm 30 ph&uacute;t để tr&aacute;nh r&eacute;t. Hiệu  trưởng Nguyễn Thu C&uacute;c cho biết đ&acirc;y l&agrave; phương &aacute;n của l&atilde;nh đạo trường nhằm hạn chế  việc c&aacute;c em phải chịu c&aacute;i lạnh v&agrave;o cuối ng&agrave;y. S&aacute;ng nay, khi nhiệt độ 8 độ C,  trường đ&atilde; th&ocirc;ng b&aacute;o đến phụ huynh v&agrave; ph&acirc;n c&ocirc;ng bảo vệ viết bảng th&ocirc;ng b&aacute;o nghỉ  đặt ở cổng trường từ s&aacute;ng sớm. C&oacute; v&agrave;i học sinh vẫn đến trường v&agrave; được bố mẹ đưa  về nh&agrave; ngay.</p>\r\n<p class=\"Normal\">\"Quy định nghỉ học khi thời tiết lạnh được Sở Gi&aacute;o dục đưa ra từ  nhiều năm nay, nh&agrave; trường cũng đ&atilde; th&ocirc;ng b&aacute;o rộng r&atilde;i đến phụ huynh n&ecirc;n t&ocirc;i thấy  kh&ocirc;ng qu&aacute; bất ngờ v&agrave; kh&oacute; giải quyết\", chị C&uacute;c n&oacute;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/phu_huynh_xem_lich_nghi_hoc.jpg\" alt=\"\" width=\"480\" height=\"350\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phụ huynh xem kỹ th&ocirc;ng b&aacute;o v&agrave; lịch học m&ugrave;a đ&ocirc;ng cho con. <em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Anh L&ecirc; Văn Ki&ecirc;n đưa con đến trường mới biết th&ocirc;ng tin được nghỉ  học, đ&agrave;nh quay xe đưa con về nh&agrave; &ocirc;ng b&agrave; ngoại gửi. Anh cho biết do ngủ dậy muộn  n&ecirc;n kh&ocirc;ng xem được chương tr&igrave;nh dự b&aacute;o thời tiết. \"Trời r&eacute;t, ch&aacute;u được nghỉ n&ecirc;n  cuộc sống gia đ&igrave;nh t&ocirc;i cũng x&aacute;o trộn. Con nghỉ học nhưng bố mẹ c&oacute; được nghỉ l&agrave;m  đ&acirc;u. Nh&agrave; t&ocirc;i c&ograve;n c&oacute; &ocirc;ng b&agrave; để gửi ch&aacute;u, chứ nhiều đồng nghiệp phải nghỉ l&agrave;m để  tr&ocirc;ng con\", anh Ki&ecirc;n n&oacute;i.</p>\r\n<p class=\"Normal\">Trao đổi với <em>VnExpress.net</em>, &ocirc;ng  Nguyễn Tr&iacute; Dũng, Ph&oacute; ph&ograve;ng Gi&aacute;o dục tiểu học (Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo) cho biết,  H&agrave; Nội rất rộng, chia l&agrave;m nhiều v&ugrave;ng, v&igrave; vậy theo chỉ đạo của Sở c&aacute;c trường được  giao quyền chủ động theo d&otilde;i thời tiết v&agrave; th&ocirc;ng b&aacute;o cho học sinh nghỉ học.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i biết c&oacute; nhiều trường v&agrave; phụ huynh cũng băn khoăn về  mức nhiệt độ để lấy l&agrave;m mốc quyết định cho học sinh nghỉ. Sở đ&atilde; th&ocirc;ng b&aacute;o l&agrave;  theo Đ&agrave;i truyền h&igrave;nh H&agrave; Nội v&agrave;o l&uacute;c 6h30. Nếu thấy trời qu&aacute; r&eacute;t, nhiệt độ dưới  10 độ C th&igrave; c&aacute;c trường tiểu học th&ocirc;ng b&aacute;o cho học sinh nghỉ để đảm bảo sức  khỏe\", &ocirc;ng Dũng n&oacute;i.</p>\r\n<p class=\"Normal\">Hiện H&agrave; Nội c&oacute; hơn 400.000 học sinh tiểu học.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1, 1013, 0, 1),
(11, 'Phương pháp Mathnasium giúp trẻ yêu thích môn toán ', 'Phuong-Phap-Mathnasium-Giup-Tre-Yeu-Thich-Mon-Toan', 'Phương pháp dạy toán Mathnasium với 5 kỹ thuật giảng dạy bổ sung nhau, giúp trẻ em tiếp thu kiến thức toán hiệu quả, không cảm thấy áp lực và nhàm chán.\r\n', 'hinh_250x195[1]_JPG_thumb210x0_ns.jpg', NULL, 22, '<h1 class=\"Title\">Phương ph&aacute;p Mathnasium gi&uacute;p trẻ y&ecirc;u th&iacute;ch m&ocirc;n  to&aacute;n</h1>\r\n<h2 class=\"Lead\">Phương ph&aacute;p dạy to&aacute;n Mathnasium với 5 kỹ thuật giảng  dạy bổ sung nhau, gi&uacute;p trẻ em tiếp thu kiến thức to&aacute;n hiệu quả, kh&ocirc;ng cảm thấy  &aacute;p lực v&agrave; nh&agrave;m ch&aacute;n.</h2>\r\n<p class=\"Normal\">Phương ph&aacute;p dạy n&agrave;y được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi  &ocirc;ng Larry Martinek - gi&aacute;o sư to&aacute;n học xuất sắc của bang California, Mỹ. Trong  trung hạn, Mathnasium gi&uacute;p học sinh ph&aacute;t triển tư duy, n&acirc;ng cao chỉ số th&ocirc;ng  minh, đồng thời x&acirc;y dựng l&ograve;ng tin v&agrave;o ch&iacute;nh bản th&acirc;n m&igrave;nh. Mục ti&ecirc;u l&acirc;u d&agrave;i v&agrave;  cao nhất của n&oacute; l&agrave; gi&uacute;p từng c&aacute; nh&acirc;n trở th&agrave;nh người tự tin, năng động v&agrave; th&agrave;nh  c&ocirc;ng trong cuộc sống.</p>\r\n<p class=\"Normal\">Trong 5 kỹ thuật giảng dạy, kỹ thuật tư duy gi&uacute;p c&aacute;c  em tập th&oacute;i quen suy nghĩ, từ đ&oacute; x&acirc;y dựng sự nhạy b&eacute;n v&agrave; tự tin với c&aacute;c con số.  Kỹ thuật diễn đạt bằng ng&ocirc;n từ l&agrave;m học sinh tập th&oacute;i quen v&agrave; kỹ năng diễn giải  bằng lời những &yacute; tưởng, suy luận của m&igrave;nh. Ngo&agrave;i ra, kỹ thuật quan s&aacute;t h&igrave;nh ảnh  trực quan, sử dụng gi&aacute;o cụ, thực h&agrave;nh to&aacute;n viết cung cấp nguồn gi&aacute;o tr&igrave;nh v&agrave; b&agrave;i  tập phong ph&uacute;, đa dạng để trẻ c&oacute; thể thực h&agrave;nh v&agrave; ph&aacute;t triển kiến thức, tư duy  v&agrave; kỹ năng giải to&aacute;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/hinh.jpg\" alt=\"\" width=\"400\" height=\"269\" /><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Gi&aacute;o tr&igrave;nh Mathnasium gồm 3 loại. Loại thứ nhất l&agrave;  c&aacute;c b&agrave;i tập PK, l&agrave; b&agrave;i tập &ldquo;theo đơn&rdquo;. Đ&acirc;y l&agrave; hệ thống c&aacute;c b&agrave;i tập chuy&ecirc;n đề  được ph&acirc;n cho từng học sinh dựa tr&ecirc;n kết quả kiểm tra đầu kỳ của học sinh đ&oacute;.  Loại b&agrave;i tập n&agrave;y gi&uacute;p ho&agrave;n thiện những mặt c&ograve;n hạn chế, v&agrave; ph&aacute;t triển c&aacute;c mặt  mạnh của từng em. Từ đ&oacute;, phương ph&aacute;p x&acirc;y dựng nền tảng to&aacute;n vững chắc cho trẻ.  Loại thứ hai l&agrave; b&agrave;i tập Work Out Book - r&egrave;n luyện tư duy số. Đ&acirc;y l&agrave; loại được  thiết kế tổng hợp, với những kh&aacute;i niệm mở rộng, gi&uacute;p học sinh ph&aacute;t triển &ldquo;tư duy  số&rdquo;, từ đ&oacute; ph&aacute;t triển tr&iacute; tuệ tốt nhất. Loại thứ ba l&agrave; Focus-on, chuy&ecirc;n s&acirc;u về  những chủ đề nhất định hoặc củng cố c&aacute;c lỗ hổng v&agrave; điểm c&ograve;n hạn chế, n&acirc;ng cao  kiến thức theo chủ đề.</p>\r\n<p class=\"Normal\">Quy tr&igrave;nh đ&agrave;o tạo của phương ph&aacute;p gồm 3 bước. Đầu  ti&ecirc;n l&agrave; bước kiểm tra đầu kỳ. Học sinh sẽ được kiểm tra viết v&agrave; vấn đ&aacute;p để x&aacute;c  định kiến thức, tr&igrave;nh độ, những điểm mạnh v&agrave; điểm cần phải củng cố, ph&aacute;t triển  th&ecirc;m. Dựa v&agrave;o kết quả n&agrave;y, kế hoạch học tập sẽ được thiết kế cho từng em. Đ&acirc;y  c&ograve;n được gọi l&agrave; phương ph&aacute;p học to&aacute;n hướng c&aacute; nh&acirc;n. Bước 2, thực h&agrave;nh dạy v&agrave; học  theo kế hoạch học tập. Trong một buổi học, mỗi gi&aacute;o vi&ecirc;n sẽ giảng dạy v&agrave; k&egrave;m cặp  4-5 học sinh theo phương ph&aacute;p dạy từ học sinh n&agrave;y đến học sinh kh&aacute;c. B&ecirc;n cạnh  đ&oacute;, thầy, c&ocirc; vẫn quan s&aacute;t, hỗ trợ học sinh khi c&aacute;c em l&agrave;m b&agrave;i tập độc lập.  Phương ph&aacute;p đảm bảo t&iacute;nh độc lập trong học tập v&agrave; sự giảng dạy k&egrave;m cặp của gi&aacute;o  vi&ecirc;n đối với từng em. Bước tiếp theo l&agrave; đ&aacute;nh gi&aacute; sự tiến bộ của học sinh. Cứ sau  3 th&aacute;ng, c&aacute;c em sẽ được kiểm tra để đ&aacute;nh gi&aacute; sự tiến bộ. Từ đ&oacute; gi&aacute;o vi&ecirc;n đưa ra  một kế hoạch học tập mới cao hơn. Quy tr&igrave;nh n&agrave;y được lặp đi lặp lại trong suốt  qu&aacute; tr&igrave;nh trẻ học với Mathnasium v&agrave; từng bước x&acirc;y dựng v&agrave; n&acirc;ng cao tư duy số v&agrave;  kiến thức to&aacute;n của từng em.</p>\r\n<p class=\"Normal\">Hiện phương ph&aacute;p n&agrave;y được &aacute;p dụng tại gần 400 trung  t&acirc;m dạy to&aacute;n Mathnasium ở Mỹ v&agrave; c&aacute;c quốc gia kh&aacute;c tr&ecirc;n thế giới. Hai trung t&acirc;m  Mathnasium đầu ti&ecirc;n tại Việt Nam ở số 3 Ng&ocirc; Thời Nhiệm, quận 3 v&agrave; 26/41-43-45  Nguyễn Minh Ho&agrave;ng, T&acirc;n B&igrave;nh, TP HCM sẽ khai giảng c&aacute;c lớp dạy to&aacute;n theo phương  ph&aacute;p Mathnasium đầu ti&ecirc;n v&agrave;o th&aacute;ng 2 (d&agrave;nh cho học sinh từ 4 đến 10 tuổi).</p>\r\n<p class=\"Normal\">Đăng k&yacute; ghi danh cho học sinh hay t&igrave;m hiểu về phương  ph&aacute;p Mathnasium, phụ huynh li&ecirc;n lạc số điện thoại 08-39301038 (quận 3),  08-38112988 (quận T&acirc;n B&igrave;nh), hay email về địa chỉ: <a class=\"Normal\" href=\"mailto:lienhe@mathnasium.vn\">lienhe@mathnasium.vn</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn: <em>C&ocirc;ng ty Ph&aacute;t triển Gi&aacute;o dục Mỹ</em>)</p>', 1, 1, 1614, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(12, 'Những nụ hôn ngọt ngào trong đêm tình nhân ', 'Nhung-Nu-Hon-Ngot-Ngao-Trong-Dem-Tinh-Nhan', 'Tối 13/2, hàng nghìn bạn trẻ có mặt tại cầu Ánh Sao (quận 7, TP HCM) chứng kiến những lời tỏ tình cùng những nụ hôn ngọt ngào của 100 cặp tình nhân trong \"Đêm Valentine thế kỷ\".\r\n', '250h_jpg_thumb210x0_ns.jpg', NULL, 22, '<h1 class=\"Title\">Những nụ h&ocirc;n ngọt ng&agrave;o trong đ&ecirc;m t&igrave;nh nh&acirc;n</h1>\r\n<h2 class=\"Lead\">Tối 13/2, h&agrave;ng ngh&igrave;n bạn trẻ c&oacute; mặt tại cầu &Aacute;nh Sao (quận 7, TP  HCM) chứng kiến những lời tỏ t&igrave;nh c&ugrave;ng những nụ h&ocirc;n ngọt ng&agrave;o của 100 cặp t&igrave;nh  nh&acirc;n trong \"Đ&ecirc;m Valentine thế kỷ\".</h2>\r\n<p><img src=\"../upload/h7.jpg\" alt=\"\" width=\"495\" height=\"330\" /></p>\r\n<p>Cầu &Aacute;nh Sao (quận 7, TP HCM) tối 13/2 l&agrave; nơi ghi lại khoảnh khắc 100 đ&ocirc;i t&igrave;nh  nh&acirc;n trao nhau những nụ h&ocirc;n ngọt ng&agrave;o.</p>\r\n<p><img src=\"../upload/h.jpg\" alt=\"\" width=\"495\" height=\"374\" /></p>\r\n<p>Đ&ocirc;i t&igrave;nh nh&acirc;n may mắn duy nhất Thế Mẫn - Thu Hương trong \"Đ&ecirc;m Valentine thế kỷ\"  được thể hiện t&igrave;nh y&ecirc;u của m&igrave;nh tr&ecirc;n khinh kh&iacute; cầu. Cả hai đ&atilde; trao nhau nụ h&ocirc;n  nồng n&agrave;n.</p>\r\n<p><img src=\"../upload/h2.jpg\" alt=\"\" width=\"495\" height=\"356\" /></p>\r\n<p>Kinh kh&iacute; cầu bay bổng đưa đ&ocirc;i bạn trẻ Thế Mẫn - Thu Hương l&ecirc;n bầu trời mịt tối.  \"C&oacute; một nụ h&ocirc;n tr&ecirc;n cao v&agrave; trong kh&ocirc;ng kh&iacute; như thế n&agrave;y th&igrave; kh&ocirc;ng g&igrave; sung sướng  bằng\", Thu Hương t&acirc;m sự.</p>\r\n<p><img src=\"../upload/h4.jpg\" alt=\"\" width=\"495\" height=\"344\" /></p>\r\n<p>H&agrave;ng trăm bạn trẻ kh&ocirc;ng c&oacute; dịp h&ocirc;n tr&ecirc;n khinh kh&iacute; cầu chia sẻ niềm vui v&agrave; ch&uacute;c  mừng hai bạn trẻ.</p>\r\n<p><img src=\"../upload/h17.jpg\" alt=\"\" width=\"495\" height=\"352\" /></p>\r\n<p>Đ&ocirc;i t&igrave;nh nh&acirc;n Qu&acirc;n v&agrave; Thương c&oacute; được những gi&acirc;y ph&uacute;t l&atilde;ng mạn.</p>\r\n<p><img src=\"../upload/h16.jpg\" alt=\"\" width=\"495\" height=\"355\" /></p>\r\n<p>V&agrave; ng&agrave;y t&igrave;nh nh&acirc;n năm nay, Thương nhận được m&oacute;n qu&agrave; 999 nụ hồng xanh do người  y&ecirc;u d&agrave;nh tặng.</p>\r\n<p><img src=\"../upload/h11.jpg\" alt=\"\" width=\"495\" height=\"373\" /></p>\r\n<p>C&ograve;n với Ngọc Yến, ng&agrave;y Valentine năm nay, người y&ecirc;u Minh Tuấn tỏ lời cầu h&ocirc;n c&ocirc;  bằng đ&ocirc;i nhẫn cưới.</p>\r\n<p><img src=\"../upload/h9.jpg\" alt=\"\" width=\"495\" height=\"367\" /></p>\r\n<p>Ngọc Yến đ&atilde; nhận lời cầu h&ocirc;n v&agrave; cả hai trao nhau nụ h&ocirc;n trước sự chứng kiến của  rất đ&ocirc;ng bạn trẻ.</p>\r\n<p><img src=\"../upload/h12.jpg\" alt=\"\" width=\"495\" height=\"359\" /></p>\r\n<p>Kim Ph&uacute;c hạnh ph&uacute;c khi người y&ecirc;u l&agrave; ch&agrave;ng trai đến từ Ph&aacute;p c&oacute; mặt tại Việt Nam  trong đ&ecirc;m đặc biệt n&agrave;y.</p>\r\n<p><img src=\"../upload/h18.jpg\" alt=\"\" width=\"495\" height=\"342\" /></p>\r\n<p>C&ocirc; g&aacute;i hạnh ph&uacute;c khi nhận m&oacute;n qu&agrave; l&aacute; đ&oacute;a hoa từ người y&ecirc;u trong đ&ecirc;m t&igrave;nh nh&acirc;n.</p>\r\n<p><img src=\"../upload/h22.jpg\" alt=\"\" width=\"495\" height=\"371\" /></p>\r\n<p>Ghi lại gi&acirc;y ph&uacute;t l&atilde;ng mạn trong đ&ecirc;m t&igrave;nh nh&acirc;n.</p>\r\n<p><img src=\"../upload/h21.jpg\" alt=\"\" width=\"495\" height=\"351\" /></p>\r\n<p>H&agrave;ng trăm đ&ocirc;i c&oacute; mặt trong \"Đ&ecirc;m Valentine thế kỷ\".</p>\r\n<p style=\"text-align: right;\"><strong>An Nhơn</strong></p>', 2, 1, 63, 0, 1),
(13, 'Hot girl tâm sự về ngày Valentine ', 'Hot-Girl-Tam-Su-Ve-Ngay-Valentine', 'Một bông hồng trắng bằng khăn giấy, chiếc xe đạp gắn đầy hoa, hay bài thơ của chàng \"thi sĩ\" vô danh gửi tặng… là những món quà đầy ấn tượng mà hot girl Midu từng nhận được trong các mùa Valentine.\r\n', 'hot-girl-valentine-2.jpg', NULL, 22, '<h1 class=\"Title\">Hot girl t&acirc;m sự về ng&agrave;y Valentine</h1>\r\n<h2 class=\"Lead\">Một b&ocirc;ng hồng trắng bằng khăn giấy, chiếc xe đạp gắn đầy hoa, hay  b&agrave;i thơ của ch&agrave;ng \"thi sĩ\" v&ocirc; danh gửi tặng&hellip; l&agrave; những m&oacute;n qu&agrave; đầy ấn tượng m&agrave;  hot girl Midu từng nhận được trong c&aacute;c m&ugrave;a Valentine.</h2>\r\n<p class=\"Normal\">Bao m&ugrave;a Valentine đ&atilde; qua nhưng đ&aacute;ng nhớ nhất đối với c&ocirc; g&aacute;i trẻ  vẫn l&agrave; kỷ niệm của mối t&igrave;nh đầu thời học sinh. Đ&oacute; l&agrave; v&agrave;o năm học cấp 3, <a class=\"Normal\" href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/05/3ba1c040/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">Midu</a> được bạn  trai hẹn đi chơi v&agrave; tặng cho hộp qu&agrave; h&igrave;nh tr&aacute;i tim m&agrave;u đỏ. H&ocirc;m đ&oacute;, c&ocirc; n&agrave;ng đ&atilde;  giận hờn, rồi nổi n&oacute;ng n&eacute;m m&oacute;n qu&agrave; xuống đường v&agrave; kh&oacute;c sướt mướt bỏ đi. L&yacute; do  chỉ v&igrave; ch&agrave;ng bắt phải chờ đợi qu&aacute; l&acirc;u, đến l&uacute;c \"t&agrave;n cuộc\" mới trao qu&agrave;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../upload/tintuc/midu21.jpg\" alt=\"Midu cho biết, Valentine đ&aacute;ng nhớ nhất của m&igrave;nh l&agrave; lần giỗi hờn ng&acirc;y thơ với bạn trai v&agrave; kh&oacute;c\" width=\"400\" height=\"598\" /></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"404\" height=\"12\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\" style=\"text-align: left;\">Midu cho biết, Valentine đ&aacute;ng nhớ nhất của m&igrave;nh l&agrave; lần dỗi hờn  ng&acirc;y thơ của mối t&igrave;nh đầu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Midu b&agrave;y tỏ: \"Nghĩ lại m&agrave; thấy buồn cười. Em rất h&aacute;o hức, n&ecirc;n  khi gặp l&agrave; muốn nhận qu&agrave; ngay, nhưng m&atilde;i chẳng thấy đ&acirc;u. Đến khi chuẩn bị về mới  l&ocirc;i ra một chiếc hộp nhỏ, giận qu&aacute; n&eacute;m lu&ocirc;n xuống đất rồi kh&oacute;c như mưa. Mỗi đứa  đi một đường. Cuối c&ugrave;ng, em lặng lẽ quay lại lượm m&agrave; kh&ocirc;ng cho người ta biết. Về  nh&agrave; mở ra th&igrave; thấy một b&ocirc;ng hồng m&agrave;u trắng l&agrave;m bằng khăn giấy k&egrave;m lời nhắn. Sau  n&agrave;y, hễ cứ nghĩ đến kỷ niệm n&agrave;y lại thấy hối hận v&igrave; biết anh ấy mất rất nhiều  thời gian v&agrave; l&agrave;m hư rất nhiều giấy mới xếp được b&ocirc;ng hoa\".</p>\r\n<p class=\"Normal\">V&agrave;o đại học, <a class=\"Normal\" href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/07/3ba1dc31/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">Midu </a>v&agrave; anh bạn đ&oacute; mỗi  người một nơi. Những m&ugrave;a Valentine tiếp theo, số lượng qu&agrave; của c&ocirc; g&aacute;i trẻ tăng  nhiều hơn khi c&ocirc; trở th&agrave;nh Hot Vteen, được nhiều người biết đến. Midu cho biết,  c&oacute; năm c&ocirc; nhận được cả một chiếc xe đạp gắn đầy hoa hồng. Qu&agrave; c&oacute; ghi t&ecirc;n người  gửi nhưng c&ocirc; kh&ocirc;ng biết r&otilde; l&agrave; ai.</p>\r\n<p class=\"Normal\">C&ograve;n bất ngờ nhất lại l&agrave; Valentine năm rồi. Trước ng&agrave;y lễ t&igrave;nh  nh&acirc;n một ng&agrave;y, một thanh ni&ecirc;n đến shop b&aacute;n h&agrave;ng của Midu v&agrave; mua tất cả m&oacute;n đồ c&oacute;  m&agrave;u t&iacute;m. Chưa kịp l&yacute; giải về h&agrave;nh động kh&aacute;c thường của vị kh&aacute;ch h&agrave;ng đặc biệt  n&agrave;y th&igrave; đ&uacute;ng v&agrave;o ng&agrave;y t&igrave;nh nh&acirc;n, tất cả m&oacute;n đồ n&agrave;y được đem đến v&agrave; tặng hết cho  c&ocirc; chủ. \"D&ugrave; kh&ocirc;ng phải l&agrave; y&ecirc;u, nhưng t&igrave;nh cảm của nhiều người l&agrave;m m&igrave;nh cảm động.  Em xem như đ&oacute; l&agrave; t&igrave;nh cảm trong s&aacute;ng n&ecirc;n kh&ocirc;ng băn khoăn khi nhận\", c&ocirc; sinh vi&ecirc;n  chuy&ecirc;n ng&agrave;nh mỹ thuật chia sẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/xuan-101.jpg\" alt=\"Bảo Ngọc th&iacute;ch người y&ecirc;u tạo cho m&igrave;nh sự bất ngờ trong ng&agrave;y lễ t&igrave;nh nh&acirc;n.\" width=\"400\" height=\"565\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bảo Ngọc th&iacute;ch người y&ecirc;u tạo cho m&igrave;nh sự bất ngờ trong ng&agrave;y lễ  t&igrave;nh nh&acirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #3f3f3f;\"><strong>Với <a href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/11/3ba22e81/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">hot girl Bảo Ngọc</a>, ng&agrave;y lễ  t&igrave;nh nh&acirc;n n&agrave;o cũng tr&agrave;n ngập niềm vui bởi nhận được rất nhiều qu&agrave;, d&ugrave; c&ocirc; chưa c&oacute;  \"mối t&igrave;nh vắt vai\" n&agrave;o.</strong></span> Ngọc cho biết, v&igrave; chưa c&oacute; bạn trai n&ecirc;n  c&ocirc; v&agrave; nh&oacute;m bạn g&aacute;i lập ra hội những người độc th&acirc;n để đi chơi v&agrave; tặng qu&agrave; cho  nhau. Nhiều người c&oacute; t&igrave;nh cảm với người đẹp đ&atilde; lặng lẽ gửi hoa, qu&agrave; k&egrave;m theo  những lời ch&uacute;c dễ thương. Với những t&igrave;nh cảm n&agrave;y, Ngọc chia sẻ c&ocirc; cảm thấy c&oacute;  ch&uacute;t ngại ng&ugrave;ng v&igrave; sợ nhận qu&agrave; rồi sẽ l&agrave;m người ta hiểu nhầm.</p>\r\n<p class=\"Normal\">Tuy ngại nhưng Bảo Ngọc kh&ocirc;ng phủ nhận c&oacute; những m&oacute;n qu&agrave; tạo ấn  tượng cho c&ocirc;. Như chiếc gối &ocirc;m nhỏ x&iacute;u c&oacute; th&ecirc;u t&ecirc;n Bảo Ngọc m&agrave; c&ocirc; nhận được c&aacute;ch  đ&acirc;y v&agrave;i năm. \"Chắc chắn người gửi đ&atilde; dồn rất nhiều t&acirc;m huyết v&agrave; t&igrave;nh cảm của  m&igrave;nh v&agrave;o m&oacute;n qu&agrave; n&agrave;y. M&oacute;n qu&agrave; nhỏ nhưng lại rất &yacute; nghĩa với em\", Ngọc n&oacute;i.</p>\r\n<p class=\"Normal\">Ngọc chia sẻ, trong ng&agrave;y Valentine c&ocirc; th&iacute;ch nhất l&agrave; cuộc hẹn bất  ngờ l&atilde;ng mạn thay v&igrave; những m&oacute;n qu&agrave; xa xỉ. \"Nếu c&oacute; người y&ecirc;u, em ước g&igrave; người đ&oacute;  sẽ tạo cho em sự bất ngờ. Chỉ cần anh ấy đến đ&oacute;n v&agrave; dẫn em đến bữa tiệc đ&atilde; sắp  đặt sẵn. Cảm gi&aacute;c l&uacute;c đ&oacute; chắc rất giống như trong truyện cổ t&iacute;ch\", c&ocirc; chia sẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/hotgirl3.jpg\" alt=\"Hot girl Elly chia sẻ đối với c&ocirc; ng&agrave;y Valentine cũng như bao ng&agrave;y kh&aacute;c bởi c&ocirc; vẫn chưa t&igrave;m được người m&igrave;nh y&ecirc;u.\" width=\"480\" height=\"356\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hot girl Elly chia sẻ, ng&agrave;y Valentine cũng như bao ng&agrave;y kh&aacute;c bởi  c&ocirc; vẫn chưa t&igrave;m được người y&ecirc;u.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #3f3f3f;\"><strong>Kh&aacute;c với cảm x&uacute;c của c&aacute;c bạn trang  lứa, <a href=\"../../gl/xa-hoi/nhip-dieu-tre/2010/03/3ba19118/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">c&ocirc; n&agrave;ng n&oacute;ng  bỏng Elly Trần</a> lại cho rằng ng&agrave;y lễ t&igrave;nh nh&acirc;n cũng b&igrave;nh thường như bao ng&agrave;y  kh&aacute;c.</strong></span> D&ugrave; nhận được rất nhiều qu&agrave; nhưng Elly th&agrave;nh thật cho biết  kh&ocirc;ng c&oacute; cảm x&uacute;c g&igrave; đặc biệt. \"Chắc bởi em chưa c&oacute; người m&igrave;nh y&ecirc;u thực sự\", c&ocirc;  n&oacute;i.</p>\r\n<p class=\"Normal\">Những m&ugrave;a Valentine gần đ&acirc;y, Elly kh&ocirc;ng may mắn được hẹn h&ograve; l&atilde;ng  mạn với bạn b&egrave; bởi ng&agrave;y n&agrave;y đều tr&ugrave;ng v&agrave;o c&aacute;c dịp c&ocirc; phải đi nước ngo&agrave;i tham gia  chụp ảnh hay giao lưu kh&aacute;n giả.</p>\r\n<p class=\"Normal\">Ấn tượng nhất với Elly l&agrave; t&igrave;nh cảm của một fan Đ&agrave;i Loan. Anh  ch&agrave;ng cất c&ocirc;ng sưu tập cả chục m&oacute;n đặc sản xứ Đ&agrave;i để l&agrave;m qu&agrave; tặng người đẹp. Anh  c&ograve;n d&agrave;nh to&agrave;n bộ số tiền tiết kiệm để mua v&eacute; m&aacute;y bay đến Việt Nam trao qua tận  tay. \"Nghe anh ấy n&oacute;i mong muốn m&oacute;n qu&agrave; sẽ gi&uacute;p em hiểu th&ecirc;m về Đ&agrave;i Loan khiến  em thấy cảm động lắm\", c&ocirc; n&oacute;i.</p>\r\n<p class=\"Normal\">Elly Trần chia sẻ, c&ocirc; đang chờ đợi một Valentine thật &yacute; nghĩa v&agrave;  l&atilde;ng mạn khi t&igrave;m được người y&ecirc;u.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&igrave;nh Nguy&ecirc;n</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 2, 1, 2768, 0, 1),
(14, 'Nên duyên chồng vợ từ mạng mai mối ', 'Nen-Duyen-Chong-Vo-Tu-Mang-Mai-Moi', 'Quen nhau qua trang web kết bạn, để chiếm được tình cảm của cô nàng cao tới 1,71 m, chàng trai cao 1,58 m kiên trì tỏ tình tới 10 lần và hạnh phúc đã mỉm cười với họ. ', 'cap_doi_hoan_hao_set_sub.jpg', NULL, 22, '<h1 class=\"Title\">N&ecirc;n duy&ecirc;n chồng vợ từ mạng mai mối</h1>\r\n<h2 class=\"Lead\">Quen nhau qua trang web kết bạn, để chiếm được t&igrave;nh cảm của c&ocirc;  n&agrave;ng cao tới 1,71 m, ch&agrave;ng trai cao 1,58 m ki&ecirc;n tr&igrave; tỏ t&igrave;nh tới 10 lần v&agrave; hạnh  ph&uacute;c đ&atilde; mỉm cười với họ. <br />&gt; <a class=\"Lead\" href=\"../../gl/xa-hoi/2009/12/3ba171fa/?phpMyAdmin=a4c14c12d106f8f87a752ed51b37018f\">T&igrave;m chồng qua net</a></h2>\r\n<p class=\"Normal\">Hội những người bạn S&agrave;i G&ograve;n v&agrave; th&agrave;nh vi&ecirc;n tr&ecirc;n một mạng x&atilde; hội  vẫn chưa ngớt b&agrave;n t&aacute;n x&ocirc;n xao về cặp đ&ocirc;i được cho l&agrave; ho&agrave;n hảo nhất. Ch&agrave;ng chỉ  cao 1,58 m, c&ograve;n n&agrave;ng hơn anh tới một c&aacute;i đầu nhưng họ vẫn y&ecirc;u nhau say đắm. T&igrave;nh  y&ecirc;u ấy được đ&aacute;nh dấu bởi một đ&aacute;m cưới linh đ&igrave;nh.</p>\r\n<p class=\"Normal\">Vừa đưa vợ đi dạo chơi nh&acirc;n ng&agrave;y Valentine về, anh Khổng Đạt  Tường (nick l&agrave; Danny) cười tươi cho biết đ&atilde; kể c&acirc;u chuyện t&igrave;nh của hai người  nhiều lần, v&agrave; mỗi lần kể đều cảm thấy ấm &aacute;p, hạnh ph&uacute;c v&igrave; nhờ mạng mai mối m&agrave;  anh đ&atilde; gặp được Jet (L&ecirc; Thị Như Huyền, hiện đ&atilde; l&agrave; vợ anh).</p>\r\n<p class=\"Normal\">Anh Tường kể, gặp Huyền trong một buổi offline của hội, tr&ograve;  chuyện v&agrave; ấn tượng với c&ocirc; g&aacute;i dễ thương c&oacute; chiều cao \"khủng\", khu&ocirc;n mặt dễ  thương, anh đ&atilde; đem l&ograve;ng cảm mến. Số lần gặp nhau nhiều hơn v&igrave; hội hay tổ chức  gặp mặt, nhiều l&uacute;c anh muốn thổ lổ t&igrave;nh cảm nhưng kh&ocirc;ng d&aacute;m mở lời v&igrave; Huyền đang  chuẩn bị cho kỳ thi tốt nghiệp cao đẳng.</p>\r\n<p class=\"Normal\">\"Kh&ocirc;ng thể kiềm chế m&atilde;i, một lần lấy hết can đảm t&ocirc;i đ&atilde; nhắn tin  cho Huyền thổ lộ rằng m&igrave;nh th&iacute;ch em mất rồi. L&uacute;c nhận được hồi &acirc;m của Huyền, t&ocirc;i  ngạt thở v&igrave; hạnh ph&uacute;c bởi \'Jet cũng mến anh\'&rdquo;, Tường t&acirc;m sự.</p>\r\n<p class=\"Normal\">Lần đầu ti&ecirc;n tỏ t&igrave;nh b&ecirc;n hồ b&aacute;n nguyệt, tim Tường như tan ra  từng mảnh khi nhận được c&acirc;u trả lời: \"Jet mến anh nhưng chỉ coi anh như người  anh b&igrave;nh thường\".</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/cap_doi_hoan_hao_1.jpg\" alt=\"\" width=\"320\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&uacute; rể 1,58 m v&agrave; c&ocirc; d&acirc;u 1,71 m hạnh ph&uacute;c b&ecirc;n nhau trong ng&agrave;y  cưới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Lời từ chối kh&ocirc;ng l&agrave;m ch&agrave;ng quản l&yacute; của một c&ocirc;ng ty du lịch bỏ  cuộc. Sau mỗi chiều tan sở, anh đều gh&eacute; qua nh&agrave; trọ thăm c&ocirc;. C&oacute; l&uacute;c đưa Huyền đi  chơi, dạo phố, đi ăn lặt vặt v&agrave; gi&uacute;p c&ocirc; định hướng tương lai. Tường tin rằng,  thời gian sẽ gi&uacute;p Huyền hiểu v&agrave; y&ecirc;u anh.</p>\r\n<p class=\"Normal\">Th&ecirc;m những lần tỏ t&igrave;nh thứ hai, thứ ba v&agrave; đến lần thứ ch&iacute;n bị từ  chối, Tường vẫn ki&ecirc;n tr&igrave; bởi nhận ra rằng Huyền đ&atilde; chiếm một vị tr&iacute; rất quan  trọng trong tr&aacute;i tim anh. Cho rằng c&oacute; thể Huyền chưa sẵn s&agrave;ng v&igrave; c&ograve;n lo cho học  tập, Tường quyết t&acirc;m đợi đến l&uacute;c c&ocirc; ra trường.</p>\r\n<p class=\"Normal\">\"Hai ng&agrave;y trước kỳ thi tốt nghiệp của Huyền, m&igrave;nh đ&atilde; rủ hai  người bạn nữa đến nh&agrave; em. M&igrave;nh tự tay nấu nồi ch&egrave; đỗ xanh với hy vọng mang đến  may mắn cho n&agrave;ng trước khi thi\", Tường kể.</p>\r\n<p class=\"Normal\">Sau khi Huyền ho&agrave;n th&agrave;nh kh&oacute;a học, Tường mạnh dạn tỏ t&igrave;nh lần  thứ 10. Thật bất ngờ, c&ocirc; n&agrave;ng cao 1,71 m đ&atilde; nhận lời y&ecirc;u ch&agrave;ng 1,58 m bởi nhận  ra anh l&agrave; người đ&agrave;n &ocirc;ng đ&iacute;ch thực, lu&ocirc;n quan t&acirc;m, lo lắng, y&ecirc;u thương c&ocirc; thật  l&ograve;ng.</p>\r\n<p class=\"Normal\">Nhưng cửa ải tiếp theo m&agrave; hai người phải vượt qua ch&iacute;nh l&agrave; sự  phản đối của gia đ&igrave;nh hai b&ecirc;n. Bố mẹ Tường khuy&ecirc;n con \"Chọn đứa cao vừa th&ocirc;i chứ  như thế th&igrave; cao qu&aacute;\", c&ograve;n gia đ&igrave;nh Huyền th&igrave; cay nghiệt: \"Bộ hết người rồi hay  sao m&agrave; lại chọn n&oacute; hả con\".</p>\r\n<p class=\"Normal\">Hai người bắt đầu l&ecirc;n kế hoạch \"lấy l&ograve;ng\" hai họ. Phụ mẫu của  Tường th&igrave; kh&aacute; dễ d&agrave;ng để thuyết phục, nhưng bố mẹ Huyền cứ thấy mặt ch&agrave;ng l&agrave; nổi  n&oacute;ng. Tường ki&ecirc;n tr&igrave; thể hiện thiện ch&iacute; bằng c&aacute;ch năng về thăm &ocirc;ng b&agrave;, thấy việc  g&igrave; c&oacute; thể l&agrave;m l&agrave; x&ocirc;ng v&agrave;o l&agrave;m ngay.</p>\r\n<p class=\"Normal\">\"Mưa dầm thấm l&acirc;u, gần một năm sau th&igrave; hai cụ đ&atilde; y&ecirc;n t&acirc;m giao  con g&aacute;i cho m&igrave;nh. Ng&agrave;y 16/11/2010 bọn m&igrave;nh đ&atilde; l&agrave;m đ&aacute;m cưới\", Tường cười cho hay.  C&ograve;n Huyền th&igrave; bật m&iacute;: \"B&acirc;y giờ bố mẹ m&igrave;nh c&ograve;n cưng anh hơn cả con g&aacute;i\".</p>\r\n<p class=\"Normal\">Valentine năm trước l&agrave; kỷ niệm 99 ng&agrave;y y&ecirc;u của đ&ocirc;i t&igrave;nh nh&acirc;n Vũ  Tuấn Đạt v&agrave; Nguyễn Thị Quỳnh. Quỳnh rất tr&acirc;n trọng ng&agrave;y n&agrave;y nhưng n&oacute; lại rơi v&agrave;o  m&ugrave;ng 1 Tết khiến hai đứa kh&ocirc;ng thể gặp nhau. Lễ t&igrave;nh nh&acirc;n năm nay, c&ocirc; đ&atilde; l&ecirc;n kế  hoạch l&atilde;ng mạn để người y&ecirc;u của m&igrave;nh thấy ấm &aacute;p nhất.</p>\r\n<p class=\"Normal\">Quỳnh kể, ngay khi đăng k&yacute; v&agrave;o mạng mai mối, c&oacute; một người thường  xuy&ecirc;n v&agrave;o ng&ocirc;i nh&agrave; ảo của Quỳnh để comment, anh bảo c&ocirc; l&agrave; \"ph&aacute;t ra y&ecirc;u kh&iacute;\", rồi  th&igrave; \"Ai m&agrave; c&oacute; thể y&ecirc;u một c&ocirc; b&eacute; thế n&agrave;y nhỉ?\". Quỳnh gh&eacute;t anh<em> </em>v&igrave; lu&ocirc;n  ch&eacute;m gi&oacute;, hay cười đểu v&agrave; đ&aacute; xo&aacute;y c&ocirc;. \"Một h&ocirc;m đang online, hai đứa nảy ra &yacute;  định gặp nhau xem ngo&agrave;i đời liệu c&oacute; \"ch&eacute;m\" như tr&ecirc;n mạng kh&ocirc;ng, vậy l&agrave; gặp\", Đạt  kể.</p>\r\n<p class=\"Normal\">Ấn tượng đầu ti&ecirc;n của Quỳnh l&agrave; c&acirc;u n&oacute;i của Đạt khi vừa nh&igrave;n thấy  đ&atilde; gọi c&ocirc; l&agrave; \"Qu&aacute;i vật\", c&ograve;n Quỳnh gọi \"anh ch&eacute;m gi&oacute;\". Kh&ocirc;ng qu&aacute; ngại ng&ugrave;ng v&igrave;  đ&atilde; hiểu nhau qua những lần tr&ograve; chuyện, buổi hẹn đầu ti&ecirc;n tr&ecirc;n phố T&ocirc; Tịch (H&agrave;  Nội) kết th&uacute;c bằng việc hai đứa cuốc bộ khi xe bị xịt lốp v&igrave; d&iacute;nh phải đinh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/cap_doi_hoan_hao_2.jpg\" alt=\"\" width=\"320\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Anh ch&agrave;ng ch&eacute;m gi&oacute; v&agrave; c&ocirc; bạn g&aacute;i hotmilk dễ thương đ&atilde; y&ecirc;u nhau  được hơn một năm nhờ t&igrave;nh cờ quen nhau tr&ecirc;n mạng mai mối. Ảnh: <em>Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Như c&oacute; sự sắp đặt, Quỳnh lại được ph&acirc;n c&ocirc;ng l&agrave;m một chương tr&igrave;nh  quảng c&aacute;o ngay tại ĐH B&aacute;ch khoa H&agrave; Nội, nơi Đạt đang theo học. Hai người gặp lại  nhau, Đạt trở th&agrave;nh hướng dẫn vi&ecirc;n giới thiệu về ng&ocirc;i trường của m&igrave;nh cho Huyền  nghe v&agrave; đưa c&ocirc; đi ăn cơm sinh vi&ecirc;n.</p>\r\n<p class=\"Normal\">Những lần đi chơi của hai người d&agrave;y l&ecirc;n, địa điểm được lựa chọn  cũng l&atilde;ng mạn. \"Một lần đi chơi, Đạt kh&ocirc;ng như mọi ng&agrave;y, n&oacute;i &iacute;t v&agrave; chỉ nh&igrave;n xa  xăm để rồi đột ngột đặt l&ecirc;n m&ocirc;i m&igrave;nh một nụ h&ocirc;n. Đ&uacute;ng 0 giờ đ&ecirc;m đ&oacute;, anh nhắn tin  tỏ t&igrave;nh\", Quỳnh chia sẻ.</p>\r\n<p class=\"Normal\">Vẫn thường gọi nhau l&agrave; \"qu&aacute;i vật\", l&agrave; \"ch&eacute;m gi&oacute;\", nhưng hai \"kẻ  th&ugrave; kh&ocirc;ng đội trời chung\" ấy đ&atilde; y&ecirc;u nhau l&uacute;c n&agrave;o kh&ocirc;ng biết. Họ c&ugrave;ng nhau đi ăn  b&uacute;n b&ograve; Huế cay x&egrave;, c&ugrave;ng nhau nấu những bữa cơm ấm c&uacute;ng, c&ugrave;ng lang thang chụp ảnh  hay c&ugrave;ng x&ograve;e tay xem b&oacute;i cho nhau.</p>\r\n<p class=\"Normal\">Khi mới nhận lời y&ecirc;u, ch&agrave;ng trai B&aacute;ch khoa đ&atilde; phải l&ecirc;n Ph&uacute; Thọ  thực tập, c&ograve;n c&ocirc; sinh vi&ecirc;n ĐH Phương Đ&ocirc;ng ở lại H&agrave; Nội với nỗi nhớ nhung. Gọi  điện, nhắn tin như chẳng đủ n&oacute;i l&ecirc;n t&igrave;nh cảm của hai người, họ c&ograve;n hẹn nhau l&ecirc;n  mạng để được \"ch&eacute;m gi&oacute;\" như ng&agrave;y n&agrave;o.</p>\r\n<p class=\"Normal\">Hơn một năm y&ecirc;u nhau, kỷ niệm t&igrave;nh y&ecirc;u của đ&ocirc;i bạn trẻ n&agrave;y c&oacute;  thể ghi đầy một cuốn sổ. Lần đầu ti&ecirc;n về nh&agrave; Quỳnh, kh&ocirc;ng hiểu sao \"con ngựa  sắt\" của Đạt giở chứng kh&ocirc;ng chịu nổ m&aacute;y. \"Chẳng hiểu g&igrave; nhưng m&igrave;nh thử lấy dao  chọc ngo&aacute;y v&agrave; n&oacute; chạy thật, vậy m&agrave; bố mẹ Quỳnh kh&ocirc;ng ngớt lời khen\", Đạt  cười.</p>\r\n<p class=\"Normal\">C&ograve;n Quỳnh th&igrave; ngay lần đầu ra mắt, trong l&uacute;c nấu ăn đ&atilde; l&agrave;m vỡ  chiếc đĩa lớn. C&ocirc; đ&atilde; nhanh ch&oacute;ng \"phi tang\" chiếc đĩa v&igrave; lo lắng bố mẹ anh ph&aacute;t  hiện v&agrave; quy kết c&ocirc; vụng về. Sau đ&oacute;, Đạt cũng đứng ra nhận hộ việc l&agrave;m vỡ đĩa cho  c&ocirc;.</p>\r\n<p class=\"Normal\">\"Thật ấm &aacute;p biết bao khi mỗi s&aacute;ng m&ugrave;a đ&ocirc;ng lại nhận được tin  nhắn \"trời lạnh rồi đấy, em mặc th&ecirc;m &aacute;o ấm nh&eacute;\". M&igrave;nh lu&ocirc;n cảm ơn mạng x&atilde; hội v&igrave;  nhờ n&oacute; m&agrave; m&igrave;nh v&agrave; anh đ&atilde; y&ecirc;u nhau\", Quỳnh t&acirc;m sự.</p>\r\n<p class=\"Normal\">Hiện tại, Đạt v&agrave; Quỳnh đ&atilde; ra trường v&agrave; c&oacute; việc l&agrave;m ổn định. Hai  người dự định sẽ l&agrave;m đ&aacute;m cưới v&agrave;o cuối năm nay.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 1, 2206, 0, 1),
(15, 'Những món quà Valentine làm từ tình yêu ', 'Nhung-Mon-Qua-Valentine-Lam-Tu-Tinh-Yeu', 'Cặm cụi cả tuần để thêu móc chìa khóa bằng len tặng người yêu, làm tranh bằng chính những hạt đỗ \"kỷ niệm ngày quen nhau\" của hai đứa là cách mà giới trẻ đang làm để tặng người yêu dịp lễ Valentine.\r\n', 'socola_set_sub.jpg', NULL, 22, '<h1 class=\"Title\">Những m&oacute;n qu&agrave; Valentine l&agrave;m từ t&igrave;nh y&ecirc;u</h1>\r\n<h2 class=\"Lead\">Cặm cụi cả tuần để th&ecirc;u m&oacute;c ch&igrave;a kh&oacute;a bằng len tặng người y&ecirc;u,  l&agrave;m tranh bằng ch&iacute;nh những hạt đỗ \"kỷ niệm ng&agrave;y quen nhau\" của hai đứa l&agrave; c&aacute;ch  m&agrave; giới trẻ đang l&agrave;m để tặng người y&ecirc;u dịp lễ Valentine.</h2>\r\n<p class=\"Normal\">Y&ecirc;u nhau được 2 năm, cả hai đều đ&atilde; c&oacute; c&ocirc;ng việc ổn định v&agrave; chỉ  chờ ng&agrave;y tốt, giờ tốt để l&agrave;m đ&aacute;m cưới, Trịnh Thị V&acirc;n (quận Hai B&agrave; Trưng, H&agrave; Nội)  rất tr&acirc;n trọng ng&agrave;y lễ t&igrave;nh nh&acirc;n h&agrave;ng năm. Với c&ocirc;, đ&acirc;y vừa l&agrave; ng&agrave;y gặp mặt đầu  ti&ecirc;n, vừa l&agrave; ng&agrave;y kỷ niệm t&igrave;nh y&ecirc;u của hai người.</p>\r\n<p class=\"Normal\">Ba năm trước, c&ocirc; v&agrave; nh&oacute;m bạn độc th&acirc;n thường rủ nhau đi c&agrave; ph&ecirc;  để ch&uacute;c mừng lẫn nhau ng&agrave;y lễ t&igrave;nh y&ecirc;u. Tại đ&acirc;y c&ocirc; gặp một ch&agrave;ng sinh vi&ecirc;n năm  cuối đang chơi ghi ta. M&ecirc; mẩn trước tiếng đ&agrave;n của anh, c&ocirc; đ&atilde; viết giấy nhờ anh  đ&aacute;nh hộ b&agrave;i \"C&acirc;y đ&agrave;n sinh vi&ecirc;n\". Đ&oacute; l&agrave; ng&agrave;y đ&aacute;nh dấu cho những lần gặp bất ngờ  về sau.</p>\r\n<p class=\"Normal\">\"Bọn m&igrave;nh gặp nhau tại qu&aacute;n, rồi hẹn nhau sau giờ tan trường.  Một năm sau, đ&uacute;ng ng&agrave;y Valentine th&igrave; ch&uacute;ng m&igrave;nh nhận lời y&ecirc;u nhau\", V&acirc;n kể.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/valentine.jpg\" alt=\"\" width=\"320\" height=\"450\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều c&ocirc; g&aacute;i d&agrave;nh thời gian th&ecirc;u tặng người y&ecirc;u những m&oacute;n qu&agrave;  đặc biệt. Ảnh: <em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Năm n&agrave;o c&ocirc; cũng d&agrave;nh những m&oacute;n qu&agrave; bất ngờ cho anh. C&oacute; năm l&agrave;  chiếc gối b&ocirc;ng, c&oacute; năm l&agrave; chiếc khăn... tất cả đều do c&ocirc; tự l&agrave;m. Năm nay, V&acirc;n  quyết định th&ecirc;u một chiếc m&oacute;c ch&igrave;a kh&oacute;a xinh xắn cho người y&ecirc;u v&igrave; \"m&oacute;c ch&igrave;a kh&oacute;a  c&oacute; thể lu&ocirc;n ở b&ecirc;n cạnh anh\".</p>\r\n<p class=\"Normal\">Bỏ 20.000 đồng mua nguy&ecirc;n liệu, tối n&agrave;o sau giờ ăn cơm c&ocirc; cũng  chạy t&oacute;t l&ecirc;n ph&ograve;ng th&ecirc;u bức tranh tr&ecirc;n mặt m&oacute;c ch&igrave;a kh&oacute;a. C&ocirc; cho hay, mất khoảng  một tuần để ho&agrave;n thiện sản phẩm.</p>\r\n<p class=\"Normal\">\"Mất nhiều thời gian nhưng m&igrave;nh thấy rất vui. Mỗi n&eacute;t chỉ th&ecirc;u  l&agrave; t&igrave;nh cảm của m&igrave;nh được gửi gắm trong đ&oacute;\", V&acirc;n tủm tỉm cười.</p>\r\n<p class=\"Normal\">Y&ecirc;u nhau 360 ng&agrave;y, B&ugrave;i Lan Huệ đ&atilde; d&ugrave;ng 360 hạt đỗ để đ&aacute;nh dấu kỷ  niệm t&igrave;nh cảm của hai người. Mua một chiếc b&igrave;nh thủy tinh lớn đặt tr&ecirc;n b&agrave;n, từ  ng&agrave;y y&ecirc;u anh, mỗi ng&agrave;y Huệ đều cho v&agrave;o b&igrave;nh một hạt đỗ. Ng&agrave;y vui c&ocirc; cho một hạt  đỗ đỏ, ng&agrave;y buồn, x&iacute;ch m&iacute;ch với nhau th&igrave; c&ocirc; bỏ một hạt đỗ đen.</p>\r\n<p class=\"Normal\">V&agrave; Valentine n&agrave;y Huệ sẽ d&ugrave;ng k&eacute;o d&aacute;n những hạt đỗ n&agrave;y th&agrave;nh bức  tranh h&igrave;nh tr&aacute;i tim, những b&ocirc;ng hoa v&agrave; một ng&ocirc;i nh&agrave; để tặng người y&ecirc;u.</p>\r\n<p class=\"Normal\">\"Nh&igrave;n v&agrave;o tranh anh sẽ nhớ đến tất cả những vui, buồn trong suốt  thời gian b&ecirc;n cạnh m&igrave;nh. Đ&oacute; l&agrave; chứng nh&acirc;n của t&igrave;nh y&ecirc;u m&agrave; hai đứa d&agrave;nh cho  nhau\", Huệ chia sẻ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/socola.jpg\" alt=\"\" width=\"450\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tự l&agrave;m chocolate để tặng người y&ecirc;u l&agrave; lựa chọn của rất nhiều bạn  trẻ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Năm n&agrave;o cũng thế, trước Valentine một tuần l&agrave; Anh Tuấn (ĐH B&aacute;ch  khoa) lại l&ecirc;n kế hoạch đi chơi v&agrave; chọn qu&agrave; cho bạn g&aacute;i. Năm nay, ng&agrave;y lễ t&igrave;nh  y&ecirc;u tr&uacute;ng v&agrave;o ng&agrave;y thứ Hai n&ecirc;n Tuấn sẽ rủ bạn g&aacute;i đi chơi v&agrave;o ng&agrave;y Chủ nhật. Sau  chuyến đi c&ocirc;ng vi&ecirc;n, hai người sẽ kết th&uacute;c ng&agrave;y Valentine bằng bữa tối l&atilde;ng  mạn.</p>\r\n<p class=\"Normal\">\"M&igrave;nh sẽ vẫn chọn những m&oacute;n qu&agrave; truyền thống l&agrave; hoa hồng v&agrave;  chocolate để tặng người y&ecirc;u. Nhưng chocolate sẽ do m&igrave;nh tự l&agrave;m với những h&igrave;nh  th&ugrave; đặc biệt. Chắc chắn người y&ecirc;u m&igrave;nh sẽ cảm động lắm\", Tuấn cười.</p>\r\n<p class=\"Normal\">Mua chocolate th&ocirc;, sữa đặc v&agrave; chiếc khu&ocirc;n với những h&igrave;nh xinh  xắn. Tuấn đun c&aacute;ch thủy chocolate, quấy đều với dầu ăn v&agrave; sữa rồi đổ v&agrave;o khu&ocirc;n  đợi kh&ocirc;. Vậy l&agrave; cậu đ&atilde; c&oacute; m&oacute;n qu&agrave; như mong muốn cho bạn g&aacute;i.</p>\r\n<p class=\"Normal\">\"Valentine l&agrave; cơ hội để ch&uacute;ng m&igrave;nh thể hiện t&igrave;nh y&ecirc;u đối với  nhau, h&acirc;m n&oacute;ng lại t&igrave;nh y&ecirc;u sau m&ugrave;a đ&ocirc;ng lạnh gi&aacute;. Sẽ thật hạnh ph&uacute;c khi được  c&ugrave;ng người y&ecirc;u thưởng thức chocolate ngọt ng&agrave;o do ch&iacute;nh tay m&igrave;nh l&agrave;m ra\", Tuấn  t&acirc;m sự.</p>\r\n<p class=\"Normal\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 1, 2731, 0, 1),
(16, '100 cặp tình nhân hôn nhau trên khinh khí cầu ', '100-Cap-Tinh-Nhan-Hon-Nhau-Tren-Khinh-Khi-Cau', '100 cặp tình nhân sẽ trao nhau nụ hôn trên khinh khí cầu và được tặng một bó hoa với 999 nụ hồng xanh, nhận \"lời cầu hôn của thần Cupid\"... trong lễ hội Valentine sẽ được tổ chức tại cầu Ánh Sao (quận 7, TP HCM) tối 13/2.\r\n', 'valentine22.jpg', NULL, 22, '<h2 class=\"Lead\">100 cặp t&igrave;nh nh&acirc;n sẽ trao nhau nụ h&ocirc;n tr&ecirc;n khinh kh&iacute; cầu v&agrave; được  tặng một b&oacute; hoa với 999 nụ hồng xanh, nhận \"lời cầu h&ocirc;n của thần Cupid\"... trong  lễ hội Valentine sẽ được tổ chức tại cầu &Aacute;nh Sao (quận 7, TP HCM) tối 13/2.</h2>\r\n<p class=\"Normal\">C&oacute; xuất xứ từ văn h&oacute;a phương T&acirc;y, lễ t&igrave;nh nh&acirc;n đ&atilde; trở th&agrave;nh một  trong những ng&agrave;y hội lớn của giới trẻ khắp nơi tr&ecirc;n thế giới. Ở Việt Nam,  Valentine ng&agrave;y c&agrave;ng c&oacute; &yacute; nghĩa quan trọng trong giới trẻ, đặc biệt l&agrave; những cặp  đ&ocirc;i đang y&ecirc;u.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/valentine-ngot-ngao.jpg\" alt=\"\" width=\"380\" height=\"499\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c cặp đ&ocirc;i sẽ trao nhau nụ h&ocirc;n tr&ecirc;n khinh kh&iacute;  cầu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đ&ecirc;m Valentine thế kỷ lần đầu ti&ecirc;n được tổ chức tr&ecirc;n cầu &Aacute;nh Sao  (quận 7, TP HCM) sẽ đưa c&aacute;c bạn trẻ v&agrave;o thế giới t&igrave;nh y&ecirc;u với mưa bong b&oacute;ng,  vườn hoa hồng xanh được thiết kế như cổ t&iacute;ch.</p>\r\n<p class=\"Normal\">Tại lễ hội T&igrave;nh nh&acirc;n n&agrave;y, 100 cặp t&igrave;nh nh&acirc;n may mắn khắp cả nước  sẽ được tặng 5 m&oacute;n qu&agrave; th&uacute; vị. Theo đ&oacute;, c&aacute;c c&ocirc; g&aacute;i sẽ được bạn trai trao một b&oacute;  hồng xanh với 999 b&ocirc;ng. Từng cặp t&igrave;nh nh&acirc;n sẽ được ban nhạc Flamenco h&aacute;t tặng  ri&ecirc;ng bản t&igrave;nh ca họ y&ecirc;u th&iacute;ch. Đặc biệt, c&aacute;c ch&agrave;ng trai sẽ trao \"người t&igrave;nh  trăm năm\" nụ h&ocirc;n tr&ecirc;n khinh kh&iacute; cầu... C&ocirc; g&aacute;i may mắn nhất trong đ&ecirc;m Valentine  sẽ nhận được chiếc nhẫn cầu h&ocirc;n lấp l&aacute;nh tr&ecirc;n tay \"vị thần Cupid\".</p>\r\n<p class=\"Normal\">Để c&oacute; mặt trong lễ hội 100 cặp t&igrave;nh nh&acirc;n, 99 cặp t&igrave;nh nh&acirc;n phải  thể hiện \"c&acirc;u chuyện t&igrave;nh y&ecirc;u\" ấn tượng nhất trong c&aacute;c phần thi trước đ&oacute; do một  c&ocirc;ng ty tổ chức. Một cặp v&eacute; c&ograve;n lại được đưa ra đấu gi&aacute; v&agrave; đ&atilde; c&oacute; một đ&ocirc;i t&igrave;nh  nh&acirc;n mua với mức 9 triệu đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/anhsao.jpg\" alt=\"\" width=\"480\" height=\"360\" /><br /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cầu &Aacute;nh Sao (quận 7, TP HCM). <em>Nguồn  Internet.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Song song đ&oacute;, cặp đ&ocirc;i Wanbi Tuấn Anh v&agrave; Trương Quỳnh Anh sẽ lần  đầu ti&ecirc;n chia sẻ c&acirc;u chuyện t&igrave;nh y&ecirc;u của họ sau lần chạm tr&aacute;n trong bộ phim 3D  \"B&oacute;ng ma học đường\". V&agrave; lần đầu ti&ecirc;n,10 nhiếp ảnh gia của Việt Nam sẽ &ldquo;hội tụ&rdquo;  tại Đ&ecirc;m Valentine thế kỷ để ghi dấu những khoảnh khắc t&igrave;nh y&ecirc;u đẹp nhất c&ugrave;ng  những nụ h&ocirc;n thế kỷ dưới bầu trời đ&ecirc;m. Những t&aacute;c phẩm nhiếp ảnh n&agrave;y sẽ được chọn  lọc v&agrave; triển l&atilde;m tại Bức tường đ&ecirc;m Valentine thế kỷ v&agrave;o ng&agrave;y Valentine tại Nh&agrave;  Văn h&oacute;a Thanh ni&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>T&aacute; L&acirc;m</strong></p>', 2, 1, 1287, 0, 1),
(17, 'Nhà thám hiểm 9 tuổi đặt chân tới Nam Cực ', 'Nha-Tham-Hiem-9-Tuoi-Dat-Chan-Toi-Nam-Cuc', 'Vượt qua hành trình dài nhiều ngày, Phạm Vũ Thiều Quang, cậu bé 9 tuổi đã cùng bố đặt chân tới Nam Cực vào chiều mùng 1 Tết. Cậu bé đã trở thành người châu Á trẻ tuổi nhất đặt chân tới vùng đất này.\r\n', 'top-2.jpg', NULL, 22, '<h1 class=\"Title\">Nh&agrave; th&aacute;m hiểm 9 tuổi đặt ch&acirc;n tới Nam Cực</h1>\r\n<h2 class=\"Lead\">Vượt qua h&agrave;nh tr&igrave;nh d&agrave;i nhiều ng&agrave;y, Phạm Vũ Thiều Quang, cậu b&eacute; 9  tuổi đ&atilde; c&ugrave;ng bố đặt ch&acirc;n tới Nam Cực v&agrave;o chiều m&ugrave;ng 1 Tết. Cậu b&eacute; đ&atilde; trở th&agrave;nh  người ch&acirc;u &Aacute; trẻ tuổi nhất đặt ch&acirc;n tới v&ugrave;ng đất n&agrave;y.</h2>\r\n<p class=\"Normal\">Chia sẻ tr&ecirc;n facebook của m&igrave;nh, anh Phạm Quang Vinh (bố của  Thiều Quang) cho biết, thời điểm Thiều Quang bước ch&acirc;n l&ecirc;n Nam Cực l&agrave; v&agrave;o l&uacute;c  15h15 ng&agrave;y 3/2. Tuy nhi&ecirc;n, chỉ trước đ&oacute; &iacute;t giờ, khả năng n&agrave;y vẫn c&ograve;n để ngỏ v&igrave;  phụ thuộc ho&agrave;n to&agrave;n v&agrave;o điều kiện thời tiết.</p>\r\n<p class=\"Normal\">Chỉ v&agrave;i giờ trong lần đầu đổ bộ, họ đ&atilde; nhận đủ cả mưa, tuyết  rơi, nắng, thậm ch&iacute; cả trời xanh trong vắt, rồi lại mưa, lại sương m&ugrave;... Theo  anh Vinh, đ&acirc;y l&agrave; lần đầu ti&ecirc;n Thiều Quang được đưa tay hứng những b&ocirc;ng  tuyết.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/13.jpg\" alt=\"Ảnh: Phạm Quang Vinh.\" width=\"490\" height=\"356\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bố con Thiều Quang trong gi&acirc;y ph&uacute;t đặt ch&acirc;n l&ecirc;n Nam Cực. Ảnh:  <em>Phạm Quang Vinh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Qua những h&igrave;nh ảnh v&agrave; chia sẻ của &ocirc;ng bố n&agrave;y, Thiều Quang đ&atilde;  vượt qua những cơn say s&oacute;ng, nhanh ch&oacute;ng tươi tỉnh khi bước ch&acirc;n l&ecirc;n mảnh đất  Nam Cực, h&aacute;o hức d&ugrave;ng chiếc m&aacute;y ảnh của m&igrave;nh ghi lại h&igrave;nh ảnh ở nơi đ&acirc;y.</p>\r\n<p class=\"Normal\">\"Thiều Quang đ&atilde; được thấy cả sư tử biển v&agrave; chim c&aacute;nh cụt nhảy  s&oacute;ng từ tr&ecirc;n t&agrave;u, v&agrave; gặp những con chim c&aacute;nh cụt Gentoo đầu ti&ecirc;n tr&ecirc;n đảo  Deception, cả mấy con sư tử biển đang l&ecirc;n bờ nằm nghỉ nữa&hellip;\", anh Vinh kể. Đ&acirc;y sẽ  l&agrave; những nh&acirc;n vật trong bộ phim ngắn m&agrave; cậu b&eacute; dự định thực hiện từ trước khi  khởi h&agrave;nh.</p>\r\n<p class=\"Normal\">L&agrave; th&agrave;nh vi&ecirc;n nhỏ tuổi nhất trong chuyến đi, Thiều Quang được cả  đo&agrave;n th&aacute;m hiểm rất y&ecirc;u qu&yacute;. Em thậm ch&iacute; c&ograve;n trở th&agrave;nh vị kh&aacute;ch đặc biệt của  thuyền trưởng con t&agrave;u MS Fram.</p>\r\n<p class=\"Normal\">Với việc tới Nam Cực, cậu b&eacute; lớp 4 Thiều Quang đ&atilde; trở th&agrave;nh  nguời ch&acirc;u &Aacute; trẻ tuổi nhất đặt ch&acirc;n l&ecirc;n mảnh đất n&agrave;y.</p>\r\n<p class=\"Normal\">Sau khi đặt ch&acirc;n tới đảo Deception v&agrave;o ng&agrave;y 3/2, đo&agrave;n th&aacute;m hiểm  đ&atilde; lần lượt tới c&aacute;c trạm nghi&ecirc;n cứu (Base) kh&aacute;c nhau. Đặc biệt tại Base W (tr&ecirc;n  đảo Detaille), Thiều Quang lại được ưu ti&ecirc;n một lần nữa, trở th&agrave;nh người đầu  ti&ecirc;n đặt ch&acirc;n l&ecirc;n đảo Detaille. Em vinh dự được mời k&yacute; v&agrave;o sổ của Base W v&igrave; l&agrave;  trẻ em đầu ti&ecirc;n tr&ecirc;n thế giới tới được địa điểm n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/10.jpg\" alt=\"Ảnh: Phạm Quang Vinh.\" width=\"490\" height=\"325\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tr&ecirc;n chuyến t&agrave;u tới mảnh đất địa cực, cậu b&eacute; n&agrave;y đ&atilde; trải qua  những cơn say s&oacute;ng. Ảnh: <em>Phạm Quang Vinh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo anh Vinh, đo&agrave;n th&aacute;m hiểm thật sự may mắn v&igrave; kh&ocirc;ng phải  chuyến du h&agrave;nh n&agrave;o đến Nam Cực cũng c&oacute; thể đến được Base W. Ngay cả trong thời  tiết rất tốt như trong ng&agrave;y 7/2, t&agrave;u MS Fram cũng phải neo ở kh&aacute; xa bờ, gần một  km để mọi người d&ugrave;ng xuồng chuy&ecirc;n dụng đi v&agrave;o bờ.</p>\r\n<p class=\"Normal\">Sau Base W, Thiều Quang c&ugrave;ng bố sẽ tiếp tục đặt ch&acirc;n tới Base E  v&agrave; Base Y... Chuyến th&aacute;m hiểm dự kiến k&eacute;o d&agrave;i trong 2 tuần qua c&aacute;c v&ugrave;ng đất của  ch&acirc;u lục lạnh gi&aacute; n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#ffffce\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">\"Trong giờ học buổi s&aacute;ng nay về an to&agrave;n v&agrave; c&aacute;c quy định của  IAATO v&agrave; Hiệp ước Antarctica ch&uacute;ng t&ocirc;i được nhắc lại l&agrave; c&oacute; 3 c&acirc;u hỏi sẽ kh&ocirc;ng c&oacute;  c&acirc;u trả lời trong những chuyến đi Nam Cực, đ&oacute; l&agrave; &ldquo;Khi n&agrave;o ch&uacute;ng ta đến đấy&rdquo;;  &ldquo;Ch&uacute;ng ta sẽ ở đấy bao l&acirc;u&rdquo; v&agrave; &ldquo;Ch&uacute;ng ta c&oacute; chắc chắn sẽ xuống đấy kh&ocirc;ng&rdquo;. Tất  cả mọi thứ phụ thuộc v&agrave;o thời tiết v&agrave; t&igrave;nh trạng của băng. Những g&igrave; ch&uacute;ng t&ocirc;i  biết h&agrave;ng ng&agrave;y l&agrave; kế hoạch A, v&agrave; c&ograve;n c&oacute; kế hoạch B, C v&agrave; D&hellip; cho từng ng&agrave;y, v&agrave;  cho cả những ng&agrave;y kế tiếp...\"</p>\r\n<p class=\"Normal\">\"M&ugrave;a h&egrave; ở Nam Cực khiến đảo Deception kh&ocirc;ng giống một v&ugrave;ng đất  Nam Cực lắm, khi bang tuyết chỉ che phủ những đỉnh n&uacute;i, c&ograve;n ch&acirc;n n&uacute;i th&igrave; lộ ra  rải r&aacute;c những bộ xương c&aacute; voi khổng lổ, những con thuyền gỗ đ&atilde; ở đ&acirc;y từ đầu thế  kỷ 20, v&agrave; cả những ng&ocirc;i mộ của c&aacute;c chiến binh v&agrave; những người đ&atilde; hy sinh ở  đ&acirc;y&hellip;\"</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i rời đảo Deception trong trạng th&aacute;i l&acirc;ng l&acirc;ng, trong  đ&oacute; c&oacute; cả việc m&igrave;nh cảm thấy thật may mắn, khi ở tr&ecirc;n bờ đ&atilde; c&oacute; những l&uacute;c nắng rất  đẹp, c&oacute; tuyết rơi&hellip; V&agrave; hơn cả, với t&ocirc;i, l&agrave; khi nghe con trai m&igrave;nh n&oacute;i &ldquo;Small step  of a child, but a big step of the children&rdquo;. Con trai t&ocirc;i đ&atilde; đến Nam Cực v&agrave;o  ng&agrave;y mồng một Tết như t&ocirc;i hứa, v&agrave; thời tiết đ&atilde; ủng hộ cho lời hứa đấy của  t&ocirc;i...\"</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(<em>Tr&iacute;ch facebook của anh Phạm Quang  Vinh</em>)</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 2, 1, 2899, 0, 1),
(18, 'Giới trẻ nô nức xin chữ đầu năm ', 'Gioi-Tre-No-Nuc-Xin-Chu-Dau-Nam', '9h sáng mùng 4 Tết dòng người kéo đến Văn miếu Quốc Tử Giám đông nghẹt. Nhiều bạn trẻ đứng chen chân hàng tiếng đồng hồ để xin được chữ: Trạng nguyên, Đỗ đạt, Trí, Nhân... khi xuân về.\r\n', 'tre-1.jpg', NULL, 22, '<h1 class=\"Title\">Giới trẻ n&ocirc; nức xin chữ đầu năm</h1>\r\n<h2 class=\"Lead\">9h s&aacute;ng m&ugrave;ng 4 Tết d&ograve;ng người k&eacute;o đến Văn miếu Quốc Tử Gi&aacute;m đ&ocirc;ng  nghẹt. Nhiều bạn trẻ đứng chen ch&acirc;n h&agrave;ng tiếng đồng hồ để xin được chữ: Trạng  nguy&ecirc;n, Đỗ đạt, Tr&iacute;, Nh&acirc;n... khi xu&acirc;n về.</h2>\r\n<p><img src=\"../upload/giam-1.jpg\" alt=\"\" width=\"490\" height=\"364\" /></p>\r\n<p>Cả trăm người xếp h&agrave;ng tại Văn miếu Quốc Tử Gi&aacute;m để mua v&eacute; l&uacute;c 9h s&aacute;ng.</p>\r\n<p><img src=\"../upload/giam-2.jpg\" alt=\"\" width=\"490\" height=\"343\" /></p>\r\n<p>Nh&oacute;m học sinh trường Thanh Oai B phải bắt xe bu&yacute;t đi từ s&aacute;ng sớm. Họ tỏ ra h&aacute;o  hức khi sở hữu được những tấm v&eacute; v&agrave;o cửa.</p>\r\n<p><img src=\"../upload/giam-4.jpg\" alt=\"\" width=\"490\" height=\"331\" /></p>\r\n<p><img src=\"../upload/giam-6.jpg\" alt=\"\" width=\"490\" height=\"351\" /></p>\r\n<p>B&ecirc;n trong s&acirc;n, cả trăm người chen ch&acirc;n để xin chữ.</p>\r\n<p><img src=\"../upload/giam-7.jpg\" alt=\"\" width=\"490\" height=\"331\" /></p>\r\n<p>C&aacute;c &ocirc;ng đồ phải k&ecirc; 8 b&agrave;n l&agrave;m việc nhưng vẫn kh&ocirc;ng đ&aacute;p ứng kịp nhu cầu của kh&aacute;ch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/giam-5.jpg\" alt=\"\" width=\"490\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nguyễn B&aacute; Hiệp (&aacute;o trắng, học sinh lớp 7 trường THCS Nam Trung  Y&ecirc;n, H&agrave; Nội) c&ugrave;ng Nguyễn B&aacute; Hiếu khoe nhau những chữ t&acirc;m đắc m&agrave; họ xin được.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><img src=\"../upload/giam-8.jpg\" alt=\"\" width=\"490\" height=\"348\" /></p>\r\n<p>Ho&agrave;ng Minh Diệp, học sinh lớp 12 trường THPT Thăng Long bảo sau nhiều năm sống ở  H&agrave; Nội nhưng đ&acirc;y l&agrave; lần đầu c&ocirc; đặt ch&acirc;n đến Văn miếu để xin chữ. \"Năm nay em dự  định thi Đại học Kinh tế Quốc d&acirc;n n&ecirc;n đến đ&acirc;y để xin chữ Tr&iacute;. Mong rằng, kết quả  thi sẽ được như mong muốn\", Diệp n&oacute;i.</p>\r\n<p><img src=\"../upload/giam-9.jpg\" alt=\"\" width=\"490\" height=\"341\" /></p>\r\n<p>C&ograve;n với Nguyễn Trung Ki&ecirc;n, cậu học sinh lớp 9 th&igrave; vui mừng cho biết sau 2 tiếng  chen ch&acirc;n cậu cũng xin được chữ \"Trạng Nguy&ecirc;n\". Ki&ecirc;n khoe sang đ&acirc;y kh&ocirc;ng chỉ c&oacute;  m&igrave;nh c&oacute; k&eacute;o th&ecirc;m v&agrave;i người bạn nữa đi c&ugrave;ng.</p>\r\n<p><img src=\"../upload/giam-23.jpg\" alt=\"\" width=\"490\" height=\"357\" /></p>\r\n<p>Nhiều bạn trẻ t&acirc;m sự với <em>VnExpress.net</em>, họ đến Văn miếu kh&ocirc;ng chỉ xin  chữ Tr&iacute;, Dũng m&agrave; cũng cố xin cho được chữ Nh&acirc;n. Họ giải th&iacute;ch, nếu kh&ocirc;ng c&oacute;  \"Nh&acirc;n\", d&ugrave; t&agrave;i giỏi mấy cũng kh&ocirc;ng trở th&agrave;nh được một con người ho&agrave;n thiện.</p>\r\n<p><img src=\"../upload/giam-10.jpg\" alt=\"\" width=\"490\" height=\"349\" /></p>\r\n<p><img src=\"../upload/giam-11.jpg\" alt=\"\" width=\"490\" height=\"303\" /></p>\r\n<p>Những kiểu hong chữ cho ch&oacute;ng kh&ocirc; của c&aacute;c bạn trẻ.</p>\r\n<p style=\"text-align: right;\"><strong>Ho&agrave;ng Anh</strong></p>', 2, 1, 2314, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(19, 'Mong ước đầu năm của giới trẻ ', 'Mong-Uoc-Dau-Nam-Cua-Gioi-Tre', 'Trong năm mới, chàng thủ khoa ĐH Ngoại thương Tăng Văn Bình quyết tâm trau dồi ngoại ngữ để thực hiện ước mơ du học, còn Miss Teen Diễm Trang sẽ dành nhiều thời gian hơn cho hoạt động xã hội, giao lưu quốc tế.\r\n', 'diem-trang-2.jpg', NULL, 22, '<h1 class=\"Title\">Mong ước đầu năm của giới trẻ</h1>\r\n<h2 class=\"Lead\">Trong năm mới, ch&agrave;ng thủ khoa ĐH Ngoại thương Tăng Văn B&igrave;nh quyết  t&acirc;m trau dồi ngoại ngữ để thực hiện ước mơ du học, c&ograve;n Miss Teen Diễm Trang sẽ  d&agrave;nh nhiều thời gian hơn cho hoạt động x&atilde; hội, giao lưu quốc tế.</h2>\r\n<h2 class=\"Lead\">Tăng Văn B&igrave;nh, thủ khoa ĐH Ngoại thương H&agrave; Nội: Trau dồi ngoại  ngữ để thực hiện ước mơ du học</h2>\r\n<div class=\"Normal\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Tang_Van_Binh.jpg\" alt=\"Tăng Văn B&igrave;nh.\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thủ khoa Tăng Văn B&igrave;nh. <em>Ảnh: Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">L&agrave; học sinh duy nhất đạt điểm tuyệt đối  trong kỳ thi đại học 2010, Tăng Văn B&igrave;nh (Đ&ocirc; Lương, Nghệ An) được mệnh danh l&agrave;  thủ khoa của c&aacute;c thủ khoa. Sau bốn th&aacute;ng theo học ĐH Ngoại thương, B&igrave;nh đ&atilde; từng  bước l&agrave;m quen với cuộc sống mới, phương ph&aacute;p học mới v&agrave; c&oacute; th&ecirc;m nhiều người  bạn.</p>\r\n</div>\r\n<p class=\"Normal\">\"Em đ&atilde; trưởng th&agrave;nh l&ecirc;n rất nhiều, kh&ocirc;ng c&ograve;n \"g&agrave; g&ocirc;\" như ng&agrave;y  xưa nữa. Đại học Ngoại thương kh&ocirc;ng chỉ là m&ocirc;̣t đi&ecirc;̉m đ&ecirc;́n tr&ecirc;n chặng đường  học t&acirc;̣p m&agrave; c&ograve;n là m&ocirc;̣t gia đình mới, m&ocirc;̣t t&ocirc;̉ &acirc;́m thực sự trong em\", B&igrave;nh  t&acirc;m sự.</p>\r\n<p class=\"Normal\">Ch&agrave;ng thủ khoa đang cố gắng học th&ecirc;m ngoại ngữ để c&oacute; thể thực  hiện ước mơ du học trong một ng&agrave;y gần nhất. B&igrave;nh đ&atilde; l&ecirc;n một kế hoạch chi tiết  cho việc học, v&agrave; đặt những c&aacute;i mốc, những mục ti&ecirc;u để cố gắng. B&ecirc;n cạnh trau dồi  kiến thức, cậu cũng học hỏi th&ecirc;m từ thầy c&ocirc; v&agrave; c&aacute;c bạn nhiều kỹ năng sống để  ho&agrave;n thiện bản th&acirc;n.</p>\r\n<p class=\"Normal\">B&igrave;nh chia sẻ, trong suốt những ng&agrave;y diễn ra Đại hội Đảng XI vừa  qua, cậu lu&ocirc;n theo d&otilde;i th&ocirc;ng tin qua b&aacute;o, đ&agrave;i. B&igrave;nh sung sướng khi đọc những  d&ograve;ng th&ocirc;ng tin th&ocirc;ng b&aacute;o sự th&agrave;nh c&ocirc;ng của Đại hội. Cậu mong rằng, sau mỗi kỳ  đại hội, đất nước ta sẽ c&oacute; một chiến lược mới cho sự ph&aacute;t triển kinh tế.</p>\r\n<p class=\"Normal\">B&igrave;nh cũng hy vọng, những nh&agrave; l&atilde;nh đạo đất nước sẽ đưa ra được  nhiều ch&iacute;nh s&aacute;ch để ph&aacute;t hiện, bồi dưỡng, sử dụng nh&acirc;n t&agrave;i. \"Sự quan t&acirc;m của c&aacute;c  cấp l&atilde;nh đạo ch&iacute;nh l&agrave; động lực để cho những người trẻ ch&uacute;ng em quyết t&acirc;m phấn  đấu v&agrave; cố gắng. B&ecirc;n cạnh đ&oacute;, em mong rằng, giới trẻ cũng sẽ được tạo mọi điều  kiện, cơ hội để cống hiến sức trẻ, nhiệt huyết v&agrave; t&agrave;i năng của m&igrave;nh, g&oacute;p phần  x&acirc;y dựng đất nước\", B&igrave;nh n&oacute;i.</p>\r\n<h2 class=\"Lead\">Thu Nga, Hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội: Ưu ti&ecirc;n h&agrave;ng đầu cho việc học  tập</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Thu_Nga.jpg\" alt=\"\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hoa kh&ocirc;i Thu Nga. <em>Ảnh: Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Bước v&agrave;o năm thứ ba đại học, Thu Nga bận rộn hơn với việc học  tập bởi c&ocirc; phải học cả ng&agrave;y ở trường, về nh&agrave; lại b&ugrave; đầu với c&aacute;c b&agrave;i tập chuy&ecirc;n  ng&agrave;nh. X&aacute;c định việc quan trọng nhất thời điểm n&agrave;y l&agrave; việc học, Nga ki&ecirc;n quyết  từ chối những việc l&agrave;m th&ecirc;m ảnh hưởng đến học tập.</p>\r\n<p class=\"Normal\">\"Em gần như kh&ocirc;ng c&oacute; thời gian rảnh v&igrave; ngo&agrave;i việc học ở trường,  em c&ograve;n tham gia học th&ecirc;m c&aacute;c m&ocirc;n học kh&oacute;, học th&ecirc;m c&aacute;c kỹ năng mềm để chuẩn bị  h&agrave;nh trang cho ng&agrave;y ra trường, đi l&agrave;m được tốt\", Nga chia sẻ.</p>\r\n<p class=\"Normal\">Mặc d&ugrave; vậy, Thu Nga vẫn h&agrave;o hứng với những chuyến đi t&igrave;nh  nguyện, từ thiện v&igrave; cộng đồng. \"Em vừa đến với đồng b&agrave;o miền Trung dịp mưa lũ.  Em rất đau l&ograve;ng v&igrave; b&agrave; con m&igrave;nh c&ograve;n rất nhiều người đ&oacute;i khổ. Nếu em c&oacute; thể l&agrave;m g&igrave;  để g&oacute;p ch&uacute;t sức nhỏ, nhất định em sẽ cố gắng hết sức, xứng đ&aacute;ng l&agrave; h&igrave;nh ảnh đại  diện của sinh vi&ecirc;n thủ đ&ocirc;\", Nga n&oacute;i.</p>\r\n<p class=\"Normal\">Dưới con mắt của một sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng,  Thu Nga kỳ vọng trong năm mới, nền kinh tế của đất nước sẽ c&oacute; sự tăng trưởng  tốt, phục hồi sau khủng hoảng, từ đ&oacute; cải thiện được đời sống của người d&acirc;n v&agrave;  tạo th&ecirc;m nhiều việc l&agrave;m cho người lao động.</p>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, c&ocirc; hy vọng, nh&agrave; nước, Bộ Gi&aacute;o dục cũng c&oacute; những cải  c&aacute;ch mạnh mẽ, tạo n&ecirc;n một nền gi&aacute;o dục mạnh v&agrave; sạch, kh&ocirc;ng c&ograve;n bạo lực trong học  đường, để nh&agrave; trường l&agrave; nơi ươm mầm những chủ nh&acirc;n cho tương lai của đất nước.  Nga tin rằng c&aacute;c bạn trẻ ng&agrave;y nay đủ bản lĩnh, lu&ocirc;n khao kh&aacute;t s&aacute;ng tạo, học hỏi  v&agrave; trải nghiệm. Ch&iacute;nh họ sẽ l&agrave;m n&ecirc;n nhiều kỳ t&iacute;ch.</p>\r\n<p class=\"Normal\">\"Nước ta c&ograve;n rất nhiều ho&agrave;n cảnh kh&oacute; khăn, bất hạnh, em mong  rằng giới trẻ sẽ l&agrave; những người đầu ti&ecirc;n sẵn l&ograve;ng sẻ chia, chung tay g&oacute;p sức từ  những h&agrave;nh động b&eacute; nhỏ của m&igrave;nh\", Nga n&oacute;i.</p>\r\n<h2 class=\"Lead\">Miss Teen Diễm Trang: D&agrave;nh nhiều thời gian hơn cho c&aacute;c hoạt động  x&atilde; hội</h2>\r\n<div class=\"Normal\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Diem-Trang.jpg\" alt=\"Diễm Trang\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Diễm Trang. Ảnh: <em>H.S.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">2010 l&agrave; một năm nhiều  bất ngờ v&agrave; đ&aacute;ng nhớ với Diễm Trang khi c&ocirc; vừa đạt danh hiệu &Aacute; kh&ocirc;i của ĐH Quốc  tế ĐH Quốc gia TP HCM vừa trở th&agrave;nh Miss Teen năm 2010. Trang tỏ ra h&agrave;i l&ograve;ng v&igrave;  những th&agrave;nh quả m&igrave;nh đ&atilde; đạt được trong năm qua v&agrave; cảm thấy bản th&acirc;n c&oacute; sự thay  đổi lớn trong suy nghĩ v&agrave; t&iacute;nh c&aacute;ch.</p>\r\n</div>\r\n<p class=\"Normal\">&ldquo;Từ một c&ocirc; g&aacute;i lu&ocirc;n tự ti giờ em đ&atilde; t&igrave;m thấy được gi&aacute; trị bản  th&acirc;n, từ một người nh&uacute;t nh&aacute;t giờ trở n&ecirc;n mạnh dạn hơn, từ một c&ocirc; b&eacute; hay c&oacute; c&aacute;i  nh&igrave;n ti&ecirc;u cực em đ&atilde; biết c&aacute;ch sống lạc quan hơn... v&agrave; từ một c&ocirc; sinh vi&ecirc;n b&igrave;nh  thường giờ lại l&agrave; Miss Teen Việt Nam. Nhưng hơn hết l&agrave; em vui v&igrave; c&oacute; thể l&agrave;m cho  gia đ&igrave;nh tự h&agrave;o về m&igrave;nh&rdquo;, Trang n&oacute;i.</p>\r\n<p class=\"Normal\">Nữ sinh cho biết trong năm mới c&oacute; rất nhiều dự định. Trước mắt  l&agrave; hưởng một c&aacute;i Tết thật vui b&ecirc;n gia đ&igrave;nh v&agrave; ngay sau đ&oacute; Trang sẽ tập trung  nhiều hơn v&agrave;o việc tham gia c&aacute;c hoạt động x&atilde; hội. Gần nhất l&agrave; việc tham gia v&agrave;o  ban tổ chức sự kiện &ldquo;Hội nghị ph&aacute;t triển c&aacute;c nh&agrave; l&atilde;nh đạo v&agrave; trao đổi văn h&oacute;a&rdquo;  với quy m&ocirc; ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh dương diễn ra 7 ng&agrave;y v&agrave;o th&aacute;ng 2. Trong đ&oacute; c&oacute; nhiều  chương tr&igrave;nh giao lưu d&agrave;nh cho sinh vi&ecirc;n của 20 nước ch&acirc;u &Aacute; với sinh vi&ecirc;n Việt  Nam. Trang b&agrave;y tỏ mong muốn sự kiện n&agrave;y sẽ l&agrave; cơ hội cho c&aacute;c bạn trẻ Việt Nam  học hỏi v&agrave; trao đổi văn h&oacute;a với c&aacute;c nước kh&aacute;c.</p>\r\n<p class=\"Normal\">Diễm Trang cũng nu&ocirc;i tham vọng học giỏi hơn nữa trong năm mới v&agrave;  h&igrave;nh ảnh của m&igrave;nh sẽ trở n&ecirc;n gần gũi th&acirc;n thiết hơn với c&aacute;c bạn học sinh, sinh  vi&ecirc;n cả nước v&agrave; c&oacute; thể mang lại những ảnh hưởng t&iacute;ch cực cho c&aacute;c bạn trẻ.</p>\r\n<p class=\"Normal\">Nữ sinh n&agrave;y cho rằng giới trẻ cần cố gắng học hỏi nhiều hơn v&agrave;  c&oacute; chọn lọc, phải qu&ecirc;n đi c&aacute;i t&ocirc;i c&aacute; nhận để nh&igrave;n nhận được những điểm chưa tốt  của bản th&acirc;n từ đ&oacute; c&oacute; thể khắc phục v&agrave; ho&agrave;n thiện m&igrave;nh từng ng&agrave;y. Mạo hiểm chấp  nhận th&aacute;ch thức v&agrave; rủi ro để nắm bắt những cơ hội học hỏi, hơn hết tạo cho bản  th&acirc;n một niềm tin v&agrave;o cuộc sống để c&oacute; thể sống v&agrave; cố gắng hết m&igrave;nh từng  ng&agrave;y.</p>\r\n<h2 class=\"Lead\">Lưu Nguyễn Hồng Quang: Tiếp tục theo đuổi đam m&ecirc; nghi&ecirc;n cứu h&oacute;a  học.</h2>\r\n<p class=\"Normal\">Hai năm li&ecirc;n tiếp gi&agrave;nh huy chương bạc Olympic H&oacute;a quốc tế, Hồng  Quang được tuyển thẳng v&agrave;o đại học, hiện l&agrave; sinh vi&ecirc;n năm nhất khoa h&oacute;a ĐH Khoa  học tự nhi&ecirc;n ĐHQG TP HCM. Mặc d&ugrave; đ&atilde; cố gắng v&agrave; đạt nhiều th&agrave;nh t&iacute;ch trong học  tập, xong Quang vẫn cảm thấy cần phải r&egrave;n luyện sự cẩn thận v&agrave; t&acirc;m l&yacute; thoải m&aacute;i  trước những bước ngoặt lớn trong cuộc sống v&agrave; c&ocirc;ng việc để đạt kết quả cao  hơn.</p>\r\n<p class=\"Normal\">Trong khi nhiều bạn b&egrave; lựa chọn c&aacute;c ng&agrave;nh kinh tế, ngoại thương,  c&ocirc;ng nghệ Quang vẫn quyết định theo đuổi theo đam m&ecirc; của m&igrave;nh để được học v&agrave;  nghi&ecirc;n cứu về H&oacute;a. \"Khi m&igrave;nh đ&atilde; kh&ocirc;ng th&iacute;ch c&ocirc;ng việc n&agrave;o đ&oacute; th&igrave; kh&ocirc;ng thể to&agrave;n  t&acirc;m, to&agrave;n &yacute; với n&oacute;. Điều đ&oacute; c&oacute; thể l&agrave;m ảnh hưởng đến cả những người xung quanh.  Với em bất cứ l&agrave; ng&agrave;nh n&agrave;o m&agrave; m&igrave;nh c&oacute; niềm đam m&ecirc; th&igrave; n&oacute; cũng th&agrave;nh c&ocirc;ng\", Quang  n&oacute;i.</p>\r\n<p class=\"Normal\">Nửa học kỳ l&agrave;m sinh vi&ecirc;n c&oacute; nhiều thay đổi, nhưng Quang kh&ocirc;ng  cảm thấy qu&aacute; bỡ ngỡ v&igrave; may mắn khi l&agrave; học sinh của trường phổ th&ocirc;ng Năng khiếu  thuộc ĐHQG TP HCM, Quang đ&atilde; được l&agrave;m quen với m&ocirc;i trường đại học. Điều kh&aacute;c biệt  r&otilde; rệt hơn đối với em l&agrave; c&oacute; nhiều bạn b&egrave; từ khắp nơi trong nước v&agrave; việc học theo  phương ph&aacute;p nghi&ecirc;n cứu l&agrave; chủ yếu, thầy c&ocirc; chỉ đ&oacute;ng vai tr&ograve; l&agrave; người định hướng.  Chia sẻ về kế hoạch trong năm mới, Quang cho biết đ&atilde; chuẩn bị tinh thần để đi du  học ng&agrave;nh H&oacute;a tại Mỹ v&agrave; trong tương lai xa mong muốn trở th&agrave;nh một nh&agrave; nghi&ecirc;n  cứu khoa học về lĩnh vực n&agrave;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n - Ho&agrave;ng  Th&ugrave;y</strong></p>', 2, 1, 2694, 0, 1),
(20, 'Giám đốc tuổi mèo và thành tích đáng nể ', 'Giam-Doc-Tuoi-Meo-Va-Thanh-Tich-Dang-Ne', 'Học hết lớp 9, Nguyễn Hữu Năm phải nghỉ học vì nhà nghèo lại đông con, nhưng chàng trai đất Chương Mỹ (Hà Nội) đã xuất sắc giành nhiều giải thưởng sáng tạo trẻ và hiện là chủ công ty chuyên về chế tạo máy.\r\n', 'Nguyen_Huu_Nam_set_sub.jpg', NULL, 22, '<h1 class=\"Title\">Gi&aacute;m đốc tuổi m&egrave;o v&agrave; th&agrave;nh t&iacute;ch đ&aacute;ng nể</h1>\r\n<h2 class=\"Lead\">Học hết lớp 9, Nguyễn Hữu Năm phải nghỉ học v&igrave; nh&agrave; ngh&egrave;o lại đ&ocirc;ng  con, nhưng ch&agrave;ng trai đất Chương Mỹ (H&agrave; Nội) đ&atilde; xuất sắc gi&agrave;nh nhiều giải thưởng  s&aacute;ng tạo trẻ v&agrave; hiện l&agrave; chủ c&ocirc;ng ty chuy&ecirc;n về chế tạo m&aacute;y.</h2>\r\n<p class=\"Normal\">Tuổi Đinh M&atilde;o, Nguyễn Hữu Năm lu&ocirc;n thể hiện l&agrave; \"ch&uacute; m&egrave;o\" chăm  chỉ. Ngay từ nhỏ, gi&uacute;p đỡ bố mẹ việc đồng &aacute;ng cũng như l&agrave;m th&ecirc;m m&acirc;y tre đan, Năm  lu&ocirc;n thắc mắc tại sao người n&ocirc;ng d&acirc;n phải bỏ nhiều sức lực. \"C&oacute; c&aacute;ch n&agrave;o để giảm  bớt vất vả\" l&agrave; c&acirc;u hỏi cậu học tr&ograve; ngh&egrave;o lu&ocirc;n trăn trở.</p>\r\n<p class=\"Normal\">Th&ocirc;ng minh, ham học, nhưng v&igrave; nh&agrave; đ&ocirc;ng con (tới 6 anh chị em)  n&ecirc;n tới năm lớp 9 Năm phải nghỉ học để c&ugrave;ng bố mẹ lao động nu&ocirc;i sống gia đ&igrave;nh,  phụ c&aacute;c em ăn học. Kh&ocirc;ng được đến trường, Năm kh&ocirc;ng th&ocirc;i t&igrave;m t&ograve;i. C&aacute;i ước mơ  gi&uacute;p n&ocirc;ng d&acirc;n bớt khổ lu&ocirc;n &acirc;m ỉ trong cậu. Bu&ocirc;ng tay việc đồng &aacute;ng, ch&agrave;ng trai  l&agrave;ng lại lấy đồ hỏng trong nh&agrave; ra th&aacute;o, lắp, t&igrave;m hiểu cấu tạo, quy tr&igrave;nh hoạt  động của n&oacute;.</p>\r\n<p class=\"Normal\">\"Những l&uacute;c kh&ocirc;ng c&oacute; đồ cũ, m&igrave;nh lấy đồ d&ugrave;ng ra th&aacute;o lắp khiến cả  nh&agrave; k&ecirc;u ầm. Vậy l&agrave; đ&agrave;nh gom tiền, ra chợ trời mua đồ về m&agrave;y m&ograve; tự học\", Năm  kể.</p>\r\n<p class=\"Normal\">Năm 2005, con robot đầu tay của Năm với t&ecirc;n gọi DN5 được gửi dự  thi \"&Yacute; tưởng s&aacute;ng tạo d&agrave;nh cho thanh thiếu ni&ecirc;n to&agrave;n quốc lần thứ nhất\". Thật  bất ngờ, robot với khả năng cẩu trục l&agrave;m bằng phế liệu v&agrave; c&aacute;c linh kiện điện tử  mua ở chợ trời đ&atilde; vượt qua 227 s&aacute;ng chế gi&agrave;nh giải nhất. Sản phẩm của Năm sau đ&oacute;  c&ograve;n được lựa chọn tham dự triển l&atilde;m s&aacute;ng tạo quốc tế tổ chức tại Ấn Độ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"../upload/Nguyen_Huu_Nam.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Năm 2010, Năm được Th&agrave;nh Đo&agrave;n H&agrave; Nội tuy&ecirc;n dương gương mặt trẻ  thủ đ&ocirc; ti&ecirc;u biểu. Ảnh: <em>Ho&agrave;ng  Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Với th&agrave;nh t&iacute;ch n&agrave;y, Năm vinh dự nhận được bằng khen của Hội đồng  Đội trung ương c&ugrave;ng Quỹ s&aacute;ng tạo kỹ thuật Việt Nam (VIFOTEC). \"Vinh dự nhất l&agrave;  sau đ&oacute; m&igrave;nh được gặp Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p\", Năm n&oacute;i.</p>\r\n<p class=\"Normal\">Gi&agrave;nh giải thưởng l&agrave; động lực tiếp th&ecirc;m sức mạnh để Năm c&agrave;ng  ki&ecirc;n tr&igrave;, m&agrave;y m&ograve; s&aacute;ng tạo những &yacute; tưởng mới. Năm 2006, cậu chế tạo robot c&oacute; chức  năng chống lật cho &ocirc;t&ocirc; mang t&ecirc;n DC26. Được l&agrave;m từ đầu video hỏng của h&agrave;ng x&oacute;m,  mấy mảnh gỗ thừa của gia đ&igrave;nh, sản phẩm n&agrave;y đ&atilde; được nhận giải khuyến kh&iacute;ch.</p>\r\n<p class=\"Normal\">Kh&ocirc;ng dừng lại ở đ&oacute;, Năm quan s&aacute;t việc l&agrave;m gỗ của những người  thợ mộc ở qu&ecirc;, thấy họ phải vất vả d&ugrave;ng sức b&agrave;o gỗ, vậy l&agrave; cậu m&agrave;y m&ograve; t&igrave;m c&aacute;ch  gi&uacute;p đỡ. Sau nhiều lần th&aacute;o lắp, năm 2006, chiếc m&aacute;y b&agrave;o gỗ ra đời. Người thợ  mộc thay v&igrave; cặm cụi l&agrave;m bằng tay đ&atilde; được m&aacute;y hỗ trợ từ b&agrave;o đến xẻ gỗ. Cậu đ&atilde; b&aacute;n  được 5 sản phẩm với gi&aacute; 750.000 đồng mỗi chiếc.</p>\r\n<p class=\"Normal\">Đầu năm 2007, nghĩ rằng c&ocirc;ng việc của m&igrave;nh cần phải \"đưa v&agrave;o quy  củ\", cậu trai l&agrave;ng 19 tuổi thuyết phục gia đ&igrave;nh cho vay vốn để mở xưởng cơ kh&iacute;.  Thương con, bố mẹ đ&atilde; phải b&aacute;n ruộng, thu được 28 triệu đồng giao cho Năm. \"Cầm  số tiền trong tay, vừa mừng, vừa tủi, t&ocirc;i quyết t&acirc;m phải cố gắng để kh&ocirc;ng phụ  l&ograve;ng bố mẹ\", Năm b&ugrave;i ng&ugrave;i.</p>\r\n<p class=\"Normal\">Năm lấy t&ecirc;n m&igrave;nh đặt cho xưởng, một mặt xoay vốn, một mặt tập  hợp thanh ni&ecirc;n trong l&agrave;ng đ&agrave;o tạo cho họ th&agrave;nh thợ h&agrave;n, thợ lắp r&aacute;p, thợ tiện...  C&ograve;n cậu tiếp tục nghi&ecirc;n cứu chiếc m&aacute;y th&aacute;i sắn gi&uacute;p b&agrave; con n&ocirc;ng d&acirc;n. Sau hơn một  năm lắp r&aacute;p, m&aacute;y đ&atilde; ra đời với c&ocirc;ng suất th&aacute;i 2 tấn một giờ. Hai cơ sở chế biến  n&ocirc;ng phẩm đặt mua với gi&aacute; 20 triệu đồng mỗi m&aacute;y. Về sau, m&aacute;y được cải tiến với  c&ocirc;ng suất cao hơn.</p>\r\n<p class=\"Normal\">Quen tay, quen nghề, Năm từng bước thiết kế m&aacute;y đ&ograve;i hỏi kỹ thuật  cao như m&aacute;y đục thủy lực, soi trục đứng... V&agrave; chỉ một năm sau khi ra đời, xưởng  cơ kh&iacute; của ch&agrave;ng trai qu&ecirc; Chương Mỹ đ&atilde; sản xuất th&agrave;nh c&ocirc;ng hơn 40 loại m&aacute;y cơ  kh&iacute;. So với m&aacute;y m&oacute;c c&oacute; c&ugrave;ng chức năng nhập khẩu từ nước ngo&agrave;i, sản phẩm của Năm  c&oacute; gi&aacute; th&agrave;nh rẻ hơn nhiều lần.</p>\r\n<p class=\"Normal\">Đầu năm nay, khi đ&atilde; mở rộng được thị trường, Năm quyết định mở  rộng xưởng, th&agrave;nh lập C&ocirc;ng ty CTM (chế tạo m&aacute;y). Cậu cũng đi học th&ecirc;m đồ họa v&agrave;  tiếng Anh để c&oacute; thể thiết kế sản phẩm tr&ecirc;n m&aacute;y t&iacute;nh.</p>\r\n<p class=\"Normal\">Với những th&agrave;nh c&ocirc;ng của m&igrave;nh, Năm sở hữu rất nhiều bằng, giấy  khen. Năm 2009, cậu được Ban chấp h&agrave;nh trung ương Đo&agrave;n trao tặng danh hiệu người  thợ trẻ giỏi to&agrave;n quốc, được chủ tịch UBND huyện Chương Mỹ tặng giấy khen v&agrave; l&agrave;  một trong 10 gương mặt trẻ thủ đ&ocirc; ti&ecirc;u biểu năm 2010.</p>\r\n<p class=\"Normal\">Bật m&iacute; về b&iacute; quyết th&agrave;nh c&ocirc;ng, Năm chia sẻ: &ldquo;M&igrave;nh kh&ocirc;ng được học  nhiều n&ecirc;n thường đến c&aacute;c xưởng cơ kh&iacute; xem họ sản xuất v&agrave; vận h&agrave;nh m&aacute;y m&oacute;c, từ đ&oacute;  về m&agrave;y m&ograve; l&agrave;m theo. M&igrave;nh nghĩ l&agrave;m việc g&igrave; cũng cần đam m&ecirc; v&agrave; cố gắng&rdquo;.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 1, 2694, 0, 1),
(21, 'Học sinh vùng cao nghỉ Tết kéo dài vì giá rét', 'Hoc-Sinh-Vung-Cao-Nghi-Tet-Keo-Dai-Vi-Gia-Ret', 'Học sinh Hà Giang có thể nghỉ Tết Tân Mão gần 20 ngày còn học sinh Lào Cai được nghỉ Tết hơn các vùng khác 3 ngày để tránh giá rét.\r\n', 'sapa9.jpg', NULL, 22, '<h1 class=\"Title\">Học sinh v&ugrave;ng cao nghỉ Tết k&eacute;o d&agrave;i v&igrave; gi&aacute; r&eacute;t</h1>\r\n<h2 class=\"Lead\">Học sinh H&agrave; Giang c&oacute; thể nghỉ Tết T&acirc;n M&atilde;o gần 20 ng&agrave;y c&ograve;n học  sinh L&agrave;o Cai được nghỉ Tết hơn c&aacute;c v&ugrave;ng kh&aacute;c 3 ng&agrave;y để tr&aacute;nh gi&aacute; r&eacute;t.</h2>\r\n<p class=\"Normal\">S&aacute;ng 8/1, trao đổi với <em>VnExpress.net</em>, &ocirc;ng Lương Văn  So&ograve;ng, Gi&aacute;m đốc Sở GD&amp;ĐT H&agrave; Giang cho biết, tỉnh n&agrave;y chưa c&oacute; lịch nghỉ Tết  cụ thể v&igrave; thời tiết chưa ổn định. Tuy nhi&ecirc;n so với mọi năm, năm nay r&eacute;t hơn,  nhất l&agrave; v&ugrave;ng n&uacute;i đ&aacute; Đồng Văn, M&egrave;o Vạc. V&igrave; thế, thời gian nghỉ Tết của học sinh  c&oacute; thể được k&eacute;o d&agrave;i gần 20 ng&agrave;y.</p>\r\n<p class=\"Normal\">Theo &ocirc;ng So&ograve;ng, ngay từ đầu năm học, Sở đ&atilde; c&oacute; kế hoạch cụ thể  cho ng&agrave;y bắt đầu v&agrave; kết th&uacute;c năm học. Học sinh H&agrave; Giang khai giảng sớm hơn cả  nước hai tuần. Nếu r&eacute;t hại k&eacute;o d&agrave;i th&igrave; c&aacute;c Ph&ograve;ng Gi&aacute;o dục tự xem x&eacute;t, đề nghị  với UBND huyện cho học sinh được nghỉ.</p>\r\n<p class=\"Normal\">\"Do học trước n&ecirc;n ch&uacute;ng t&ocirc;i kh&ocirc;ng lo chậm chương tr&igrave;nh. Mặt  kh&aacute;c, trong điều kiện gi&aacute; r&eacute;t th&igrave; việc giữ sức khỏe cho học sinh phải được xem  l&agrave; trọng t&acirc;m\", &ocirc;ng So&ograve;ng n&oacute;i.</p>\r\n<p class=\"Normal\"><img src=\"../upload/sapa9.jpg\" alt=\"\" width=\"494\" height=\"349\" /></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\">Học sinh ở Sapa đi học trong gi&aacute; r&eacute;t. Ảnh: <em>M&atilde; Anh  L&acirc;m.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Gi&aacute;m đốc Sở GD&amp;ĐT L&agrave;o Cai Trương Kim Minh cho hay d&ugrave; đ&atilde; c&oacute;  kế hoạch cho học sinh tiểu học nghỉ Tết 10 ng&agrave;y, trung học một tuần, bắt đầu từ  28 Tết, nhưng để đối ph&oacute; với thời tiết biến đổi thất thường Sở kh&ocirc;ng quy định  ng&agrave;y nghỉ cứng. C&aacute;c trường đều được trang bị biểu đo nhiệt độ ri&ecirc;ng, căn cứ v&agrave;o  nhiệt độ chiều h&ocirc;m trước, nếu thấy dưới 10 độ C th&igrave; c&oacute; thể b&aacute;o c&aacute;o cho Ph&ograve;ng  Gi&aacute;o dục để xin chỉ đạo của UBND huyện, từ đ&oacute; cho học sinh nghỉ.</p>\r\n<p class=\"Normal\">\"Ch&uacute;ng t&ocirc;i giao quyền chủ động cho c&aacute;c trường bởi chỉ dựa v&agrave;o dự  b&aacute;o thời tiết của tỉnh th&igrave; chưa đủ. V&igrave; khu vực Sapa hay &Ocirc; Qu&yacute; Hồ c&oacute; thể dưới 5  độ C, nhưng v&ugrave;ng Thanh Kim, Thanh Qu&yacute; lại tr&ecirc;n 10 độ C\", &ocirc;ng Minh n&oacute;i v&agrave; cho  biết sau Tết học sinh L&agrave;o Cai được nghỉ k&eacute;o d&agrave;i hơn nơi kh&aacute;c khoảng 3 ng&agrave;y.</p>\r\n<p class=\"Normal\">Gi&aacute;m đốc Sở khẳng định việc chống r&eacute;t t&iacute;ch cực kh&ocirc;ng phải l&agrave; cho  học sinh nghỉ học bởi L&agrave;o Cai c&oacute; những v&ugrave;ng quanh năm r&eacute;t, nếu nghỉ d&agrave;i th&igrave;  kh&ocirc;ng thể ho&agrave;n th&agrave;nh chương tr&igrave;nh. Ch&iacute;nh v&igrave; vậy, ngay từ th&aacute;ng 10/2010, Sở đ&atilde; c&oacute;  kế hoạch đ&oacute;n r&eacute;t v&agrave; r&egrave;n cho học sinh chống chọi với thời tiết.</p>\r\n<p class=\"Normal\">Việc đầu ti&ecirc;n l&agrave; c&aacute;c trường sửa chữa ph&ecirc;n, v&aacute;ch, mua bao tải về  bịt khe hở. Sở GD&amp;ĐT H&agrave; Giang c&ograve;n li&ecirc;n hệ với H&agrave; Nội k&ecirc;u gọi ủng hộ quần &aacute;o  để học sinh giữ ấm. \"Mới đ&acirc;y, Sở đ&atilde; nhận 200 khối quần &aacute;o, đồ d&ugrave;ng cho m&ugrave;a r&eacute;t  từ H&agrave; Nội gửi l&ecirc;n. Số quần &aacute;o n&agrave;y đ&atilde; được ph&acirc;n ph&aacute;t đến tận trường cho học  sinh\", &ocirc;ng Minh cho hay.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1, 1828, 0, 1),
(22, '\'Không để thí sinh đăng ký đại học như chơi xổ số\'', '', '\"Chúng ta không thể để học sinh đăng ký nguyện vọng như chơi sổ xố, đến cuối cùng mới biết kết quả. Hãy để các em thấy rõ cơ hội của mình\", Bộ trưởng GD&ĐT Phạm Vũ Luận phát biểu tại Hội nghị tuyển sinh sáng 18/2. ', 'Pham_Vu_Luan.jpg', NULL, 22, '<h1 class=\"Title\">\'Kh&ocirc;ng để th&iacute; sinh đăng k&yacute; đại học như chơi xổ số\'</h1>\r\n<h2 class=\"Lead\">\"Ch&uacute;ng ta kh&ocirc;ng thể để học sinh đăng k&yacute; nguyện vọng như chơi sổ xố, đến cuối c&ugrave;ng mới biết kết quả. H&atilde;y để c&aacute;c em thấy r&otilde; cơ hội của m&igrave;nh\", Bộ trưởng GD&amp;ĐT Phạm Vũ Luận ph&aacute;t biểu tại Hội nghị tuyển sinh s&aacute;ng 18/2.&nbsp;<br /><span style=\"font-size: 10px; font-weight: normal;\">Tại hội nghị tuyển sinh đại học, cao đẳng s&aacute;ng 17/2, đại diện nhiều trường cho biết, c&oacute; nhiều học sinh bằng v&agrave; tr&ecirc;n mức điểm s&agrave;n vẫn trượt đại học, trong khi rất nhiều trường lại kh&ocirc;ng tuyển đủ chỉ ti&ecirc;u như ĐH Đồng Th&aacute;p, ĐH T&acirc;y Nguy&ecirc;n v&agrave; trường ngo&agrave;i c&ocirc;ng lập.</span></h2>\r\n<p class=\"Normal\">Bộ trưởng Gi&aacute;o dục Đ&agrave;o tạo Phạm Vũ Luận khẳng định, năm nay c&aacute;c trường cần thực hiện tuyển nguyện vọng 2, nguyện vọng 3 theo tinh thần c&ocirc;ng khai minh bạch. Th&ocirc;ng tin tuyển sinh phải được cập nhật li&ecirc;n tục tr&ecirc;n Internet để phụ huynh v&agrave; học sinh theo d&otilde;i, nắm được t&igrave;nh h&igrave;nh từ đ&oacute; quyết định chờ kết quả hay r&uacute;t ra đăng k&yacute; nguyện vọng ở trường kh&aacute;c.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/68/fe/Pham_Vu_Luan.jpg\" alt=\"\" width=\"480\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\"Năm nay ch&uacute;ng ta sẽ tiến h&agrave;nh c&ocirc;ng khai, minh bạch để th&iacute; sinh c&oacute; thể lựa chọn c&aacute;c trường theo nguyện vọng 2, 3 thoải m&aacute;i, kh&ocirc;ng để t&igrave;nh trạng \"cơm treo, m&egrave;o đ&oacute;i\", Bộ trưởng Luận n&oacute;i. Ảnh:<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">\"Ch&uacute;ng ta kh&ocirc;ng thể để học sinh đăng k&yacute; nguyện vọng như chơi sổ xố, đến cuối c&ugrave;ng mở ra mới biết kết quả m&agrave; h&atilde;y để c&aacute;c em thấy r&otilde; cơ hội của m&igrave;nh, phải chọn trường ph&ugrave; hợp với kết quả cho đến khi n&agrave;o kh&ocirc;ng thể lựa chọn được nữa. Ch&uacute;ng ta sẽ kh&ocirc;ng hạn chế cơ hội của c&aacute;c em\", Bộ trưởng Luận n&oacute;i.</p>\r\n<p class=\"Normal\">Bộ trưởng cũng cho biết, thời gian x&eacute;t tuyển kh&ocirc;ng qu&aacute; cứng nhắc, những trường chưa tuyển đủ c&oacute; thể được tuyển th&ecirc;m thời gian khoảng một th&aacute;ng, nhất quyết kh&ocirc;ng để t&igrave;nh trạng \"cơm treo m&egrave;o nhịn đ&oacute;i\".</p>\r\n<p class=\"Normal\">TS Nguyễn Tấn Vui, Ph&oacute; hiệu trưởng ĐH T&acirc;y nguy&ecirc;n đưa ra thực tế những ng&agrave;nh rất quan trọng với đất nước, với v&ugrave;ng như N&ocirc;ng - L&acirc;m - Ngư rất kh&oacute; tuyển sinh v&agrave; hầu như năm n&agrave;o cũng thiếu chỉ ti&ecirc;u trầm trọng. &Ocirc;ng Nguyễn Văn Bản, Ph&oacute; hiệu trưởng ĐH Đồng Th&aacute;p v&agrave; đại diện ĐH H&ugrave;ng Vương cũng n&ecirc;u những kh&oacute; khăn tương tự.</p>\r\n<p class=\"Normal\">Th&ocirc;ng cảm với c&aacute;c trường, Thứ trưởng Bộ Gi&aacute;o dục B&ugrave;i Văn Ga cho rằng, trong trường hợp cần thiết sẽ hạ điểm s&agrave;n đối với những ng&agrave;nh kh&oacute; tuyển tr&ugrave;ng với những ng&agrave;nh m&agrave; đất nước đang ưu ti&ecirc;n để vừa giải quyết kh&oacute; khăn cho c&aacute;c trường, vừa tạo th&ecirc;m điều kiện cho th&iacute; sinh.</p>\r\n<p class=\"Normal\">Tại hội nghị, Bộ trưởng Phạm Vũ Luận đ&atilde; chỉ đạo một số việc cần l&agrave;m để chuẩn bị cho kỳ thi đại học, cao đẳng sắp tới như giao nhiệm vụ cho Cục khảo th&iacute; v&agrave; c&aacute;c cơ quan li&ecirc;n quan sớm ho&agrave;n thiện quy chế thi trắc nghiệm. Đối với th&iacute; sinh l&agrave; người nước ngo&agrave;i, sẽ giao quyền tuyển sinh về cho hiệu trưởng. Ri&ecirc;ng th&iacute; sinh khuyết tật, Bộ trưởng sẽ cố gắng để giao quyền cho hiệu trưởng c&aacute;c trường quyết định cho th&iacute; sinh được miễn thi hay kh&ocirc;ng.</p>\r\n<p class=\"Normal\">\"Vấn đề hậu kiểm rất quan trọng. Sau kỳ tuyển sinh, c&aacute;c trường phải kiểm tra lại b&agrave;i thi v&agrave; Bộ sẽ kiểm tra một lần cuối. Những trường n&agrave;o vi phạm sẽ bị xử l&yacute; nghi&ecirc;m khắc. Sai phạm của năm nay nhưng năm sau mới ph&aacute;t hiện cũng bị xử l&yacute;\", Bộ trưởng nhấn mạnh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/68/fe/thi_dai_hoc.jpg\" border=\"1\" alt=\"Th&iacute; sinh dự thi đại học 2010. Ảnh: Tiến Dũng\" width=\"480\" height=\"339\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh dự thi đại học 2010. Ảnh:&nbsp;<em>Tiến Dũng</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Kỳ thi đại học, cao đẳng 2011 sẽ vẫn thực hiện 3 chung: chung đề, chung đợt, chung kết quả. Đợt một di&ecirc;̃n ra trong hai ng&agrave;y 4 v&agrave; 5, đợt 2 di&ecirc;̃n ra trong hai ng&agrave;y 9 v&agrave; 10 của th&aacute;ng 7. Đợt 3 thi cao đẳng diễn ra trong hai ng&agrave;y 15 v&agrave; 16/7; c&aacute;c m&ocirc;n năng khiếu k&eacute;o d&agrave;i thời gian thi đến 22/7.</p>\r\n<p class=\"Normal\">Dự kiến thời gian nộp hồ sơ theo tuyến Sở gi&aacute;o dục sẽ bắt đầu từ ng&agrave;y 14/3 đến 14/4. C&ograve;n c&aacute;c trường đại học, cao đẳng sẽ bắt đầu nhận từ ng&agrave;y 15/4 đến hết ng&agrave;y 21/4. Để đơn giản h&oacute;a c&aacute;c thủ tục h&agrave;nh ch&iacute;nh đối với th&iacute; sinh tr&uacute;ng tuyển quy định \"nộp hồ sơ tr&uacute;ng tuyển\" khi học sinh nhập học được b&atilde;i bỏ.</p>\r\n<p class=\"Normal\">Quy chế tuyển sinh, những điều cần biết về kỳ thi đại học, cao đẳng sẽ được th&ocirc;ng b&aacute;o tr&ecirc;n website của Bộ trước ng&agrave;y 14/3. Chỉ ti&ecirc;u tuyển sinh của c&aacute;c trường đại học, cao đẳng, trung cấp chuy&ecirc;n nghiệp hệ ch&iacute;nh quy năm 2011 sẽ được x&aacute;c định tr&ecirc;n 2 ti&ecirc;u ch&iacute;: diện t&iacute;ch s&agrave;n x&acirc;y dựng tr&ecirc;n 1 sinh vi&ecirc;n (Diện t&iacute;ch s&agrave;n x&acirc;y dựng phục vụ đ&agrave;o tạo v&agrave; nghi&ecirc;n cứu khoa học d&agrave;nh cho mỗi sinh vi&ecirc;n tối thiểu l&agrave; 2m2) v&agrave; số sinh vi&ecirc;n, học sinh quy đổi tr&ecirc;n 1 giảng vi&ecirc;n, gi&aacute;o vi&ecirc;n (đối với mỗi nh&oacute;m ng&agrave;nh kh&aacute;c nhau tỷ lệ sinh vi&ecirc;n tr&ecirc;n giảng vi&ecirc;n kh&aacute;c nhau). Đối với những cơ sở đ&agrave;o tạo c&oacute; kết quả tuyển sinh năm 2009 v&agrave; 2010 thấp hơn 50% số chỉ ti&ecirc;u được Bộ giao th&ocirc;ng b&aacute;o th&igrave; chỉ ti&ecirc;u tuyển sinh ch&iacute;nh quy năm 2011 được x&aacute;c định tối đa l&agrave; 50%.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#cab5ca\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\">Những trường bị xử phạt vi phạm h&agrave;nh ch&iacute;nh trong tuyển sinh năm 2010 bao gồm: ĐH Kinh doanh v&agrave; c&ocirc;ng nghệ H&agrave; Nội, ĐH Kỹ thuật Y tế Hải Dương, ĐH Thăng Long, CĐ Kinh tế c&ocirc;ng nghiệp H&agrave; Nội, CĐ Viễn Đ&ocirc;ng, CĐ Giao th&ocirc;ng vận tải 2, CĐ X&acirc;y dựng Nam Định, CĐ B&igrave;nh Định, CĐ Y tế B&igrave;nh Thuận, CĐ H&agrave;ng Hải, CĐ X&acirc;y dựng số 2, CĐ Cộng Đồng H&agrave; Nội, CĐ T&agrave;i ch&iacute;nh hải quan, CĐ B&aacute;ch Việt v&agrave; CĐ Kinh tế, kỹ thuật thương mại.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y - Hải Duy&ecirc;n</strong></p>', 1, 1, 3, 0, 1),
(23, 'Chụp ảnh cưới tại Flamingo Đại Lải Resort ', 'Chup-Anh-Cuoi-Tai-Flamingo-Dai-Lai-Resort', 'Với hệ sinh thái độc đáo gồm đảo với đàn chim hàng nghìn con cùng nhiều hồ, suối, bán đảo… Flamingo Đại Lải Resort là điểm đến cho các đôi uyên ương muốn tìm nơi lưu giữ khoảnh khắc trăm năm và nghỉ dưỡng cuối tuần.\r\n', 'New_Imagesp2.jpg', NULL, 22, '<h1 class=\"Title\">Chụp ảnh cưới tại Flamingo Đại Lải Resort</h1>\r\n<h2 class=\"Lead\">Với hệ sinh th&aacute;i độc đ&aacute;o gồm đảo với đ&agrave;n chim h&agrave;ng ngh&igrave;n con c&ugrave;ng  nhiều hồ, suối, b&aacute;n đảo&hellip; Flamingo Đại Lải Resort l&agrave; điểm đến cho c&aacute;c đ&ocirc;i uy&ecirc;n  ương muốn t&igrave;m nơi lưu giữ khoảnh khắc trăm năm v&agrave; nghỉ dưỡng cuối tuần.</h2>\r\n<p class=\"Normal\">Flamingo Đại Lải Resort tọa lạc tr&ecirc;n c&aacute;c b&aacute;n đảo đẹp ph&iacute;a bắc hồ  Đại Lải, Vĩnh Ph&uacute;c. Nơi đ&acirc;y được bao quanh bởi n&uacute;i, hồ nước, suối v&agrave; rừng c&oacute;  diện t&iacute;ch h&agrave;ng ngh&igrave;n ha, tạo n&ecirc;n một cảnh quan thơ mộng. Kh&ocirc;ng gian xanh rộng,  tầm nh&igrave;n trải tr&ecirc;n mặt nước lớn đến tận c&aacute;c điểm nhấn cảnh quan của cả v&ugrave;ng hồ  n&uacute;i c&ugrave;ng thi&ecirc;n nhi&ecirc;n hoang sơ, gi&uacute;p c&aacute;c đ&ocirc;i bạn trẻ dễ d&agrave;ng t&igrave;m cho m&igrave;nh những  g&oacute;c ảnh đẹp v&agrave; lạ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/New_Image1.jpg\" alt=\"\" width=\"448\" height=\"298\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh những c&aacute;nh rừng th&ocirc;ng, mảng c&acirc;y hoa m&agrave;u, thảm cỏ v&agrave;  rừng cọ, bạn c&oacute; thể ghi lại h&igrave;nh ảnh đ&ocirc;i lứa trước kh&ocirc;ng gian của b&atilde;i cỏ v&agrave; b&atilde;i  tắm của Flamingo Beach Club. Xuy&ecirc;n qua c&aacute;nh rừng th&ocirc;ng, thảm cỏ v&agrave; rừng cọ,  Flamingo Beach Club đem đến cho du kh&aacute;ch kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng của hồ, b&atilde;i c&aacute;t  uốn lượn hướng ra b&aacute;n đảo xanh m&aacute;t. Biển xanh, c&aacute;t trắng, kh&ocirc;ng giang tĩnh lặng  sẽ l&agrave; nền cho những tấm ảnh cưới v&agrave; những gi&acirc;y ph&uacute;t thư gi&atilde;n của đ&ocirc;i uy&ecirc;n ương.  Ngo&agrave;i ra, hai bạn c&ograve;n c&oacute; thể dạo chơi tr&ecirc;n hồ bằng du thuyền, ngắm chim bay về  tổ v&agrave; lưu giữ c&aacute;c bức ảnh hạnh ph&uacute;c l&uacute;c ho&agrave;ng h&ocirc;n.</p>\r\n<p class=\"Normal\">Ngo&agrave;i giờ chụp ảnh, cặp vợ chồng sắp cưới cũng c&oacute; thể tận hưởng  cuộc sống v&agrave;ng son tại Flamingo Đại Lải Resort với đồi cỏ, b&atilde;i tắm tự nhi&ecirc;n,  vườn c&acirc;y ăn quả thư gi&atilde;n, con đường thơ v&agrave; thưởng thức c&aacute;c giai điệu từ hệ thống  đ&agrave;n gi&oacute; cao nhất Việt Nam.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img style=\"border: 1px solid black;\" src=\"../upload/New-Image02.jpg\" alt=\"\" width=\"448\" height=\"169\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c đ&ocirc;i uy&ecirc;n ương tới nơi đ&acirc;y cũng c&oacute; thể tham gia chơi golf tại  c&acirc;u lạc bộ Golf &amp; Thể thao hay thả m&igrave;nh tr&ecirc;n mặt hồ với c&acirc;u lạc bộ Du  thuyền. C&acirc;u lạc bộ Chăm s&oacute;c sức khỏe l&agrave; lựa chọn để đắm m&igrave;nh trong kh&ocirc;ng gian  spa hiện đại v&agrave; sang trọng. Những ai y&ecirc;u th&iacute;ch nghệ thuật c&oacute; thể thưởng thức  kh&ocirc;ng gian nghệ thuật cổ điển tại C&acirc;u lạc bộ Văn h&oacute;a &amp; Nghệ thuật hay một bộ  phim hay tại c&acirc;u lạc bộ Giải tr&iacute;.</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, bạn kh&ocirc;ng thể kh&ocirc;ng đến Flamingo Club v&agrave; Flamingo  Beach Club với c&aacute;c dịch vụ v&agrave; hoạt động giải tr&iacute; cao cấp bao gồm: ẩm thực, bơi,  ch&egrave;o thuyền kayak, xe đạp, tổ chức sự kiện, nghệ thuật, spa&hellip;</p>\r\n<p class=\"Normal\">Để đăng k&yacute; trở th&agrave;nh hội vi&ecirc;n v&agrave; sử dụng dịch vụ với nhiều ưu  đ&atilde;i tại Flamingo Club, li&ecirc;n hệ:</p>\r\n<p class=\"Normal\">Flamingo Club - Flamingo Đại Lải Resort</p>\r\n<p class=\"Normal\">Trụ sở ch&iacute;nh: X&atilde; Ngọc Thanh, Ph&uacute;c Y&ecirc;n, Vĩnh Ph&uacute;c<br />Điện thoại:  02116268689 - Fax: 02116268679<br />Website: <a class=\"Normal\" href=\"http://www.flamingodailai.com/\">http://www.flamingodailai.com/</a></p>\r\n<p class=\"Normal\">Văn ph&ograve;ng tại H&agrave; Nội: 63 H&agrave;ng Trống, Ho&agrave;n Kiếm</p>\r\n<p class=\"Normal\">Điện thoại: 0439285786 - Fax: 0439286388<br />Email: <a class=\"Normal\" href=\"mailto:sales@hvg.com.vn\">sales@hvg.com.vn</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn: <em>Flamingo Đại Lải Resort</em>)</p>', 1, 1, 2534, 0, 1),
(24, 'Hỗ trợ gần 3.000 vé xe tết cho sinh viên', 'Ho-Tro-Gan-3.000-Ve-Xe-Tet-Cho-Sinh-Vien', 'Mỗi khi máy bay sắp hạ cánh xuống sân bay Suvarnabhumi, tôi đều có cảm giác mình sắp được trở về nhà, về quê hương thân thương thứ hai của mình. Bạn Đặng Quốc Chí chia sẻ.Khoảng 2.700 sinh viên ngoại tỉnh có hoàn cảnh khó khăn, đang học tập tại TP HCM sẽ được tặng vé xe miễn phí về quê trong dịp tết Tân Mão.', 'sinhvien[1]_1.jpg', NULL, 22, '<h1 class=\"Title\">Hỗ trợ gần 3.000 v&eacute; xe tết cho sinh vi&ecirc;n</h1>\r\n<h2 class=\"Lead\">Khoảng 2.700 sinh vi&ecirc;n ngoại tỉnh c&oacute; ho&agrave;n cảnh kh&oacute; khăn, đang học tập tại TP HCM sẽ được tặng v&eacute; xe miễn ph&iacute; về qu&ecirc; trong dịp tết T&acirc;n M&atilde;o.</h2>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; một trong chuỗi c&aacute;c hoạt động hỗ trợ sinh vi&ecirc;n dịp tết của Trung t&acirc;m hỗ trợ sinh vi&ecirc;n TP HCM. Trong đ&oacute;, dự kiến sẽ d&agrave;nh 1.800 v&eacute; cho sinh vi&ecirc;n ngh&egrave;o c&oacute; hộ khẩu thường tr&uacute; tại c&aacute;c tỉnh miền Trung bị thiệt hại nặng nề trong c&aacute;c đợt b&atilde;o lũ vừa qua. 900 v&eacute; c&ograve;n lại sẽ tặng cho c&aacute;c sinh vi&ecirc;n ngh&egrave;o, sinh vi&ecirc;n khuyết tật, sinh vi&ecirc;n d&acirc;n tộc c&aacute;c tỉnh th&agrave;nh kh&aacute;c trong cả nước.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/45/10/sinhvien[1].jpg\" border=\"1\" alt=\"Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết\" width=\"480\" height=\"328\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ph&oacute; gi&aacute;m đốc trung t&acirc;m hỗ trợ sinh vi&ecirc;n Dương Trọng Ph&uacute;c cho biết, so với c&aacute;c năm trước số lượng v&eacute; hỗ trợ năm nay &iacute;t hơn nhiều v&igrave; kh&ocirc;ng c&oacute; c&aacute;c đơn vị v&agrave; doanh nghiệp đồng h&agrave;nh. Đa phần kinh ph&iacute; trong hoạt động n&agrave;y đều do Ủy ban Mặt trận tổ quốc Việt Nam TP HCM hỗ trợ.</p>\r\n<p class=\"Normal\">Số lượng v&eacute; tặng sẽ đưa xuống c&aacute;c trường theo chỉ ti&ecirc;u. Sinh vi&ecirc;n kh&oacute; khăn trong c&aacute;c diện được x&eacute;t tặng v&eacute; xe c&oacute; thể li&ecirc;n hệ trực tiếp tại văn ph&ograve;ng Đo&agrave;n thanh ni&ecirc;n - Hội sinh vi&ecirc;n, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n của trường trước ng&agrave;y 31/12 để tiến h&agrave;nh l&agrave;m hồ sơ.</p>\r\n<p class=\"Normal\">Ngo&agrave;i tặng v&eacute; xe, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n sẽ cung cấp khoảng 3.500 đầu việc với nhiều loại h&igrave;nh theo tuần, ng&agrave;y, giờ... cho c&aacute;c bạn ở lại c&oacute; nhu cầu kiếm th&ecirc;m thu nhập. Chương tr&igrave;nh vui tết xa nh&agrave; với nhiều hoạt động th&uacute; vị cũng sẽ được tổ chức cho những sinh vi&ecirc;n kh&ocirc;ng c&oacute; điều kiện về qu&ecirc;. Đại diện Th&agrave;nh Đo&agrave;n v&agrave; Trung t&acirc;m hỗ trợ sinh vi&ecirc;n cũng sẽ đi thăm hỏi, giao lưu v&agrave; tặng qu&agrave; cho sinh vi&ecirc;n trong KTX của c&aacute;c trường.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>', 1, 1, 2687, 0, 1),
(1546, 'Trung Quốc xây đô thị siêu sạch', 'Trung-Quoc-Xay-Do-Thi-Sieu-Sach', 'Người dân trong đô thị sinh thái của Trung Quốc có thể uống nước từ vòi, di chuyển trên các phương tiện giao thông không xả khí thải.', 'eco6.jpg', NULL, 22, '<p class=\"Title\">Trung Quốc x&acirc;y đ&ocirc; thị si&ecirc;u sạch</p>\r\n<p class=\"Lead\">Người d&acirc;n trong đ&ocirc; thị sinh th&aacute;i của Trung Quốc c&oacute; thể uống nước từ v&ograve;i, di chuyển tr&ecirc;n c&aacute;c phương tiện giao th&ocirc;ng kh&ocirc;ng xả kh&iacute; thải.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/1E/eco6.jpg\" alt=\"M&ocirc; h&igrave;nh th&agrave;nh phố sinh th&aacute;i gần Thi&ecirc;n T&acirc;n. Ảnh: AFP.\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">M&ocirc; h&igrave;nh th&agrave;nh phố sinh th&aacute;i gần Thi&ecirc;n T&acirc;n. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><em>AFP</em>&nbsp;cho biết, th&agrave;nh phố sinh th&aacute;i của Trung Quốc được x&acirc;y tr&ecirc;n khu đất kh&ocirc;ng thể trồng trọt do nhiễm mặn gần th&agrave;nh phố cảng Thi&ecirc;n T&acirc;n v&agrave; c&oacute; diện t&iacute;ch 30 km2. Qu&aacute; tr&igrave;nh x&acirc;y dựng sẽ diễn ra trong &iacute;t nhất 10 năm.</p>\r\n<p class=\"Normal\">C&aacute;c nh&agrave; thầu hy vọng th&agrave;nh phố sinh th&aacute;i của họ sẽ l&agrave; h&igrave;nh mẫu của một loại đ&ocirc; thị si&ecirc;u sạch, được quy hoạch tốt v&agrave; ph&aacute;t triển bền vững.</p>\r\n<p class=\"Normal\">&ldquo;Ch&uacute;ng t&ocirc;i hy vọng th&agrave;nh phố sinh th&aacute;i mới sẽ t&aacute;c động tới những nước l&aacute;ng giềng&rdquo;, Goh Chye Boon, tổng gi&aacute;m đốc điều h&agrave;nh tập đo&agrave;n đầu tư v&agrave; ph&aacute;t triển đ&ocirc; thị sinh th&aacute;i Thi&ecirc;n T&acirc;n, ph&aacute;t biểu.</p>\r\n<p class=\"Normal\">Ch&iacute;nh phủ Trung Quốc v&agrave; Singapore hợp t&aacute;c với nhau về t&agrave;i ch&iacute;nh v&agrave; kỹ thuật để x&acirc;y dựng th&agrave;nh phố sinh th&aacute;i. Theo kế hoạch th&agrave;nh phố sẽ c&oacute; 350.000 d&acirc;n với cơ sở hạ tầng to&agrave;n diện như trường học, bệnh viện v&agrave; c&aacute;c khu thương mại.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/1E/eco2.jpg\" alt=\"Một xe tải tiến v&agrave;o c&ocirc;ng trường x&acirc;y dựng đ&ocirc; thị sinh th&aacute;i gần th&agrave;nh phố Thi&ecirc;n T&acirc;n v&agrave;o ng&agrave;y 15/9. Ảnh: AFP.\" width=\"450\" height=\"281\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một xe tải tiến v&agrave;o c&ocirc;ng trường x&acirc;y dựng đ&ocirc; thị sinh th&aacute;i gần th&agrave;nh phố Thi&ecirc;n T&acirc;n v&agrave;o ng&agrave;y 15/9. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nhiều c&ocirc;ng ty nước ngo&agrave;i, như Hitachi (Nhật Bản) v&agrave; Philips (H&agrave; Lan), sẽ cung cấp c&ocirc;ng nghệ sạch cho th&agrave;nh phố. C&aacute;c t&ograve;a nh&agrave; sẽ c&oacute; tường c&aacute;ch &acirc;m v&agrave; được lắp cửa sổ c&oacute; khả năng nhận nhiều &aacute;nh s&aacute;ng để tiết kiệm điện.</p>\r\n<p class=\"Normal\">Gần hai phần ba r&aacute;c thải của d&acirc;n sẽ được t&aacute;i chế. 20% điện trong th&agrave;nh phố sẽ được sản xuất từ c&aacute;c nguồn t&agrave;i nguy&ecirc;n v&ocirc; tận v&agrave; kh&ocirc;ng g&acirc;y &ocirc; nhiễm m&ocirc;i trường như gi&oacute; v&agrave; &aacute;nh s&aacute;ng mặt trời.</p>\r\n<p class=\"Normal\">Sau khi được xử l&yacute;, nước thải sẽ chảy theo c&aacute;c k&ecirc;nh để v&agrave;o một hồ. Người d&acirc;n c&oacute; thể lấy nước trong hồ để sử dụng trong sinh hoạt h&agrave;ng ng&agrave;y.</p>\r\n<p class=\"Normal\">&ldquo;Đ&ocirc; thị sinh th&aacute;i trở th&agrave;nh nhu cầu cần thiết bởi Trung Quốc đang đối mặt với t&igrave;nh trạng &ocirc; nhiễm nghi&ecirc;m trọng trong c&aacute;c th&agrave;nh phố&rdquo;, &ocirc;ng Hiroaki Suzuki - một chuy&ecirc;n gia h&agrave;ng đầu về t&agrave;i ch&iacute;nh, kinh tế v&agrave; ph&aacute;t triển đ&ocirc; thị của Ng&acirc;n h&agrave;ng Thế giới &ndash; nhận x&eacute;t. Ng&acirc;n h&agrave;ng Thế giới l&agrave; tổ chức hỗ trợ dự &aacute;n đ&ocirc; thị sinh th&aacute;i của Trung Quốc.</p>\r\n<p class=\"Normal\">Giới chức cao cấp của Trung Quốc hy vọng đ&ocirc; thị sinh th&aacute;i sẽ gi&uacute;p họ giải quyết t&igrave;nh trạng số d&acirc;n đ&ocirc; thị tăng qu&aacute; nhanh. Kể từ khi Trung Quốc thực hiện cải c&aacute;ch kinh tế c&aacute;ch đ&acirc;y hơn 30 năm, d&acirc;n số đ&ocirc; thị nước n&agrave;y đ&atilde; tăng với tốc độ cao nhất trong lịch sử do v&agrave;i trăm triệu người di chuyển ra th&agrave;nh phố để t&igrave;m kiếm cơ hội.</p>\r\n<p class=\"Normal\">Đ&agrave;i truyền h&igrave;nh Trung Quốc cho biết, để đ&aacute;p ứng nhu cầu của cư d&acirc;n th&agrave;nh phố, Trung Quốc phải đầu tư tới 3,6 ngh&igrave;n tỷ USD v&agrave;o cơ sở hạ tầng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>', 34, 8, 2839, 0, 1);

